import { FC } from "react"
import {
  Box,
  HStack,
  Spinner,
  StackProps,
  Text,
  Tooltip,
  useBreakpointValue,
  VStack,
} from "@chakra-ui/react"
import { CardDivider } from "./_layout/CardDivider"
import { CardHeading } from "./_typography/CardHeading"
import { InformationIcon } from "./_icons"
import { Apy } from "./Apy"
import { cellarDataMap } from "data/cellarDataMap"
import { isFuture } from "date-fns"
import { apyHoverLabel, apyLabel } from "data/uiConfig"
import { useStrategyData } from "data/hooks/useStrategyData"

// Define an interface for APY data which includes the optional 'formatted' property
interface ApyData {
  formatted?: string
}

interface CellarStatsYieldProps extends StackProps {
  cellarId: string
}

export const CellarStatsYield: FC<CellarStatsYieldProps> = ({
  cellarId,
  ...rest
}) => {
  const cellarConfig = cellarDataMap[cellarId].config
  const borderColor = useBreakpointValue({
    sm: "transparent",
    md: "neutral.700",
  })

  const { data: strategyData, isLoading: isStrategyLoading } =
    useStrategyData(
      cellarConfig.cellar.address,
      cellarConfig.chain.id
    )

  const tvm = strategyData?.tvm
  const stakingEnd = strategyData?.stakingEnd
  const isStakingStillRunning =
    stakingEnd?.endDate && isFuture(stakingEnd?.endDate)
  const baseApy: ApyData = strategyData?.baseApy
    ? (strategyData.baseApy as ApyData)
    : { formatted: undefined }
  const rewardsApy: ApyData = strategyData?.rewardsApy
    ? (strategyData.rewardsApy as ApyData)
    : { formatted: undefined }
  const extraRewardsApy: ApyData = strategyData?.extraRewardsApy
    ? (strategyData.extraRewardsApy as ApyData)
    : { formatted: undefined }
  const merkleRewardsApy: number | undefined = strategyData?.merkleRewardsApy
    ? (strategyData.merkleRewardsApy)
    : undefined


  const baseApySumRewards = strategyData?.baseApySumRewards

  return (
    <HStack
      spacing={{ base: 2, md: 8 }}
      rowGap={4}
      w={{ base: "full", md: "auto" }}
      justifyContent={{ base: "space-between", md: "unset" }}
      divider={
        <CardDivider
          _last={{
            borderColor,
          }}
        />
      }
      {...rest}
    >
      <VStack spacing={1} align="center">
        <Text as="span" fontSize="21px" fontWeight="bold">
          {tvm ? `${tvm?.formatted}` : <Spinner />}
        </Text>
        <Tooltip
          hasArrow
          placement="top"
          label="Total value locked"
          bg="surface.bg"
          color="neutral.300"
        >
          <HStack spacing={1} align="center">
            <CardHeading>TVL</CardHeading>
            <InformationIcon color="neutral.300" boxSize={3} />
          </HStack>
        </Tooltip>
      </VStack>
      {baseApySumRewards && (
        <VStack spacing={1} align="center">
          <Apy
            apy={
              isStrategyLoading ? (
                <Spinner />
              ) : (
                baseApySumRewards?.formatted
              )
            }
          />
          <Box>
            <Tooltip
              hasArrow
              placement="top"
              label={
                <>
                  <Text>
                    {apyHoverLabel(cellarConfig)}{" "}
                    {baseApy?.formatted ?? "0.00%"}
                  </Text>
                  {cellarConfig.customReward?.showOnlyBaseApy !==
                    undefined &&
                  cellarConfig.customReward?.showOnlyBaseApy ===
                    true ? (
                    <></>
                  ) : (
                    <>
                      <Text>
                        {cellarConfig.customReward?.showSommRewards
                          ? `SOMM Rewards APY ${
                              rewardsApy?.formatted ?? "0.00%"
                            }`
                          : null}
                      </Text>
                      <Text>
                        {cellarConfig.customReward
                          ?.customRewardAPYTooltip ??
                          `${
                            cellarConfig.customReward?.showAPY
                              ? `${cellarConfig.customReward.tokenDisplayName} `
                              : ""
                          }Rewards APY ${
                            extraRewardsApy?.formatted ??
                            rewardsApy?.formatted ??
                            "0.00%"
                          }`}
                      </Text>
                      {/* <Text>
                        {merkleRewardsApy
                          ? `Merkle Rewards APY ${merkleRewardsApy.toFixed(2)}%`
                          : ''
                          }
                      </Text> */}
                    </>
                  )}
                </>
              }
              bg="surface.bg"
              color="neutral.300"
            >
              <HStack spacing={1} align="center">
                <CardHeading>{apyLabel(cellarConfig)}</CardHeading>
                {!!apyLabel(cellarConfig) && (
                  <InformationIcon color="neutral.300" boxSize={3} />
                )}
              </HStack>
            </Tooltip>
          </Box>
        </VStack>
      )}
    </HStack>
  )
}

import { ReactElement, FC } from "react"
import { Box, Select, SelectProps } from "@chakra-ui/react"

interface Props extends SelectProps {
  chains: string[]
}

export const ChainSelector: FC<Props> = ({
  chains,
  ...rest
}): ReactElement<any> => {
  return (
    <Select borderRadius={25} fontWeight="medium" {...rest}>
      {chains.map((chain, i) => (
        <Box as="option" color="surface.bg" key={i} value={chain}>
          {chain}
        </Box>
      ))}
    </Select>
  )
}

import { FC } from "react"
import dynamic from "next/dynamic"
import { Box, HStack, Text, useTheme, VStack } from "@chakra-ui/react"
import { formatCurrency } from "utils/formatCurrency"
const BarChart = dynamic(
  () => import("components/_charts/BarChart"),
  {
    ssr: false,
  }
)

interface CurrentDepositsProps {
  currentDeposits?: string
  cellarCap?: string
  asset?: string
}

export const CurrentDeposits: FC<CurrentDepositsProps> = ({
  currentDeposits,
  cellarCap,
  asset,
}) => {
  const theme = useTheme()
  const isDataPresent = currentDeposits && cellarCap

  const getData = () => {
    if (!isDataPresent) return []

    const minValue = parseInt(cellarCap) * 0.01
    const value = parseInt(currentDeposits)

    // Show a line even if value is very small.
    return [{ currentDeposits: value > minValue ? value : minValue }]
  }

  return (
    <VStack maxW={288} align="stretch" mt={4}>
      <HStack justify="space-between" align="flex-end">
        <Text fontSize="0.625rem" color="neutral3400">
          Current Deposits
        </Text>
        <Text fontSize="xs" fontWeight="semibold">
          ${formatCurrency(currentDeposits)}
        </Text>
      </HStack>
      <Box
        w="100%"
        h="4px"
        bg="#252429"
        borderRadius={2}
        overflow="hidden"
      >
        {/* @ts-ignore */}
        <BarChart
          layout="horizontal"
          colors={theme.colors.lime.base}
          keys={["currentDeposits"]}
          data={getData()}
          maxValue={isDataPresent ? parseInt(cellarCap) : "auto"}
          borderRadius={2}
          valueScale={{ type: "linear" }}
          enableLabel={false}
          enableGridX={false}
          enableGridY={false}
        />
      </Box>
      <HStack justify="space-between" align="flex-end">
        <Text fontSize="0.625rem" color="neutral3400">
          Cellar Cap
        </Text>
        <Text fontSize="xs" fontWeight="semibold">
          ${formatCurrency(cellarCap)}
        </Text>
      </HStack>
    </VStack>
  )
}

import { Stack, Text, VStack } from "@chakra-ui/react"
import { TransparentCard } from "components/_cards/TransparentCard"
import { LighterSkeleton } from "components/_skeleton"
import { formatUSD } from "utils/formatCurrency"
import { fetchTVLData } from "queries/get-all-tvl"
import { useEffect, useState } from "react"
import { GetTVLDataQuery } from "src/data/actions/types"
import { cellarDataMap } from "src/data/cellarDataMap"

export const Overview = () => {
  const [tvlData, setTotalTVL] = useState<GetTVLDataQuery | null>(
    null
  )
  const [isLoading, setIsLoadingTVL] = useState(false) // Added isLoadingTVL state

  useEffect(() => {
    setIsLoadingTVL(true) // Set loading state to true before fetching data
    fetchTVLData()
      .then((response) => {
        setTotalTVL(response)
      })
      .catch((error) => {
        console.log("Error from fetchTVLData", error) // Log errors
      })
      .finally(() => {
        setIsLoadingTVL(false) // Reset loading state in either case (success/failure)
      })
  }, [])

  const totalTVM: Number = tvlData?.total_tvl ?? 0

  // Just length of map cellarDataMap
  const totalLaunchedStrategies = Object.keys(cellarDataMap).length

  return (
    <TransparentCard marginTop="48px" py="30px">
      <Stack
        flexDir={{ base: "column", md: "row" }}
        justifyContent="space-around"
        alignItems="center"
        gap={{ base: 8, md: 4 }}
      >
        <VStack spacing="8px">
          <Text fontSize="16px" fontWeight="bold">
            TVL
          </Text>
          <LighterSkeleton
            isLoaded={!isLoading}
            width={isLoading ? "100px" : "auto"}
            height={isLoading ? "60px" : "auto"}
          >
            <Text fontWeight="bold" fontSize="40px">
              {formatUSD(String(totalTVM))}
            </Text>
          </LighterSkeleton>
        </VStack>
        <VStack spacing="8px">
          <Text fontSize="16px" fontWeight="bold">
            Total Vaults
          </Text>
          <LighterSkeleton
            isLoaded={!isLoading}
            width={isLoading ? "100px" : "auto"}
            height={isLoading ? "60px" : "auto"}
          >
            <Text fontWeight="bold" fontSize="40px">
              {String(totalLaunchedStrategies)}
            </Text>
          </LighterSkeleton>
        </VStack>
      </Stack>
    </TransparentCard>
  )
}

