import {
  Box,
  Heading,
  Image,
  SimpleGrid,
  Stack,
  Text,
  useDisclosure,
} from "@chakra-ui/react"
import { SecondaryButton } from "components/_buttons/SecondaryButton"
import { BaseModal } from "components/_modals/BaseModal"
import { cellarDataMap } from "data/cellarDataMap"
import { strategyPageContentData } from "data/strategyPageContentData"
import { isUseBigBacktestingModal } from "data/uiConfig"
import parse from "html-react-parser"
import { useRouter } from "next/router"
import { useState } from "react"

interface HighlightProps {
  id: string
}

export const isValidURL = (value: string) => {
  const res = value.match(
    /^(http(s)?:\/\/)[\w.-]+(?:\.[\w\.-]+)+[\w\-\._~:/?#[\]@!\$&'\(\)\*\+,;=.]+$/gm
  )
  return res !== null
}

export const Highlight = ({ id }: HighlightProps) => {
  const content = strategyPageContentData[id]
  const [expandHowItWorks, setExpandHowItWorks] = useState(false)
  const howItWorks = content.howItWorks.split("<br/><br/>")
  const { isOpen, onOpen, onClose } = useDisclosure()
  const router = useRouter()
  const cellarData = cellarDataMap[id]

  return (
    <Stack direction="column" mt={52} spacing="80px">
      <Stack spacing="40px">
        <Heading>Strategy Highlights</Heading>
        <SimpleGrid columns={{ base: 1, md: 3 }} spacing={4}>
          {content.strategyHighlights.card.map((item, index) => (
            <Box
              key={index}
              bg="rgba(78, 56, 156, 0.16)"
              px={4}
              py={10}
              rounded="xl"
            >
              <Heading size="md">{item}</Heading>
            </Box>
          ))}
        </SimpleGrid>
        <Text maxW="40rem" color="#D9D7E0">
          {content.strategyHighlights.description &&
            parse(content.strategyHighlights.description)}
        </Text>
      </Stack>
      {content.backtestingText && (
        <Stack maxW="40rem" spacing="40px">
          {isValidURL(content.backtestingText) ? (
            <Box>
              <SecondaryButton
                onClick={() => {
                  // analytics.track("strategy.view-backtesting", {
                  //   strategyCard: cellarData.name,
                  //   landingType: landingType(),
                  // })
                  router.push(content.backtestingText)
                }}
              >
                View Backtesting Data
              </SecondaryButton>
            </Box>
          ) : (
            <Box>
              <SecondaryButton
                onClick={() => {
                  // analytics.track("strategy.view-backtesting", {
                  //   strategyCard: cellarData.name,
                  //   landingType: landingType(),
                  // })
                  onOpen()
                }}
              >
                View Backtesting Data
              </SecondaryButton>
              <BaseModal
                heading="Backtesting data"
                isOpen={isOpen}
                onClose={onClose}
                size={isUseBigBacktestingModal(cellarData.config)}
              >
                {content.backtestingImage && (
                  <Image
                    src={content.backtestingImage}
                    alt="backtesting"
                  />
                )}
                <Text whiteSpace="pre-line" mt="4">
                  {parse(content.backtestingText)}
                </Text>
              </BaseModal>
            </Box>
          )}
        </Stack>
      )}
    </Stack>
  )
}

import { useEffect, useState, memo } from "react";
import {
  Container,
  Drawer,
  DrawerBody,
  DrawerCloseButton,
  DrawerContent,
  DrawerOverlay,
  Flex,
  FlexProps,
  HStack,
  IconButton,
  Image,
  Stack,
  useDisclosure,
} from "@chakra-ui/react";
import ConnectButton from "components/_buttons/ConnectButton";
import { Link } from "components/Link";
import { useRouter } from "next/router";
import { NAV_LINKS } from "utils/navLinks";
import { LogoTextIcon } from "./_icons";
import useBetterMediaQuery from "hooks/utils/useBetterMediaQuery";
import { useScrollDirection } from "hooks/utils/useScrollDirection";
import { HamburgerIcon } from "components/_icons";
import { Badge, BadgeStatus } from "./Strategy/Carousel/Badge";

export const Nav = memo((props: FlexProps) => {
  const [scrolled, setScrolled] = useState<boolean>(false);
  const scrollDirection = useScrollDirection();
  const { isOpen, onOpen, onClose } = useDisclosure();
  const routes = useRouter();
  const isLarger992 = useBetterMediaQuery("(min-width: 992px)");

  // Listen for scroll event to set state
  useEffect(() => {
    const listener = () => {
      setScrolled(window.scrollY >= 80);
    };

    window.addEventListener("scroll", listener);
    return () => {
      window.removeEventListener("scroll", listener);
      setScrolled(false);
    };
  }, []);

  const styles: FlexProps | false = scrolled && {
    bg: "rgba(26, 26, 26, 0.5)",
    backdropFilter: "blur(8px)",
    borderBottom: "1px solid",
    borderColor: "purple.dark",
  };
  const mobileScrollHide =
    (!isLarger992 && `nav ${scrollDirection === "down" && "down"}`) || undefined;

  return (
    <Flex
      className={mobileScrollHide}
      position="fixed"
      width="100%"
      as="nav"
      py="36px"
      fontSize="xl"
      zIndex="sticky"
      transition="ease-in-out 200ms"
      transitionProperty="background"
      {...styles}
      {...props}
    >
      <Container
        display="flex"
        maxW="100%"
        justifyContent="space-between"
        alignItems="center"
        flexDir="row"
        gap={{ base: 4, lg: 0 }}
        px={{ base: "16px", md: "30px", lg: "40px" }}
      >
        {/* Desktop Navigation */}
        {isLarger992 && (
          <HStack spacing={10}>
            <Link href="/">
              <LogoTextIcon w="9rem" h="2rem" />
            </Link>
            {NAV_LINKS.map((item) => {
              const isExternalLink = item.link.startsWith("http");
              let isActive = false;

              if (!isExternalLink) {
                isActive = item.link === "/" 
                  ? routes.pathname === "/" 
                  : routes.pathname.startsWith(item.link);
              } else {
                // Define a function with typed parameter to normalize URLs
                const normalizeUrl = (url: string): string =>
                  url.replace(/^(https:\/\/)?(www\.)?/, "");

                const externalLinks = [
                    "https://somm.finance/",
                    "https://somm.finance/audits",
                    "https://somm.finance/defi",
                    "https://somm.finance/staking"
                ];
                const pathSegment = routes.pathname.split("/")[1];

                // Ensure isActive is always a boolean
                isActive = externalLinks.some(link => normalizeUrl(link) === normalizeUrl(item.link))
                    ? false
                    : pathSegment === "strategies"
                    ? false // Set to false instead of empty string to maintain boolean type
                    : pathSegment === item.link.split("/")[1];

              }

              return (
                <Flex key={item.link} align="center">
                  <Link
                    href={item.link}
                    color={
                      isActive && !isExternalLink
                        ? "white"
                        : "neutral.400"
                    }
                    fontWeight="semibold"
                  >
                    {item.title}
                  </Link>
                  {item.isNew && (
                    <Badge status={BadgeStatus.New} ml={2} />
                  )}
                </Flex>
              );
            })}
          </HStack>
        )}

        {/* Mobile Navigation */}
        {!isLarger992 && (
          <>
            <IconButton
              variant="unstyled"
              aria-label={"menu"}
              display={{
                base: "flex",
                lg: "none",
              }}
              flex={1}
              justifyContent="flex-start"
              onClick={onOpen}
            >
              <HamburgerIcon />
            </IconButton>
            <Link href="/">
              <Image
                src="/assets/images/sommelier-icon.svg"
                alt="somm logo1"
                height="2rem"
              />
            </Link>
          </>
        )}

        <Flex flex={1} justifyContent="flex-end">
          <ConnectButton />
        </Flex>

        <Drawer
          placement={"left"}
          onClose={onClose}
          isOpen={isOpen && !isLarger992}
        >
          <DrawerOverlay />
          <DrawerContent backgroundColor="#1E163D">
            <DrawerCloseButton size="lg" />
            <DrawerBody p={0}>
              <Stack alignItems="flex-end" py="160px" px="24px">
                {NAV_LINKS.map((item) => {
                  const isExternalLink = item.link.startsWith("http");
                  let isActive = !isExternalLink && 
                    (item.link === "/" 
                      ? routes.pathname === "/" 
                      : routes.pathname.startsWith(item.link));

                  return (
                    <Flex key={item.link} align="center">
                      <Link
                        href={item.link}
                        color={
                          isActive && !isExternalLink
                            ? "white"
                            : "neutral.400"
                        }
                        fontWeight="semibold"
                      >
                        {item.title}
                      </Link>
                      {item.isNew && (
                        <Badge status={BadgeStatus.New} ml={2} />
                      )}
                    </Flex>
                  );
                })}
              </Stack>
            </DrawerBody>
          </DrawerContent>
        </Drawer>
      </Container>
    </Flex>
  );
}
);

