          <HStack>
            {((isLoading || isEnsLoading) && <Spinner size="xs" />) ||
              undefined}

            {ensName
              ? isLarger480
                ? ensName
                : truncateString(ensName)
              : truncateWalletAddress(address, isLarger480 ? 3 : 2)}
            {walletAddressIcon()}
            <ChevronDownIcon />
          </HStack>
