                "td:first-of-type": {
                  borderRadius: "32px 0 0 32px",
                  overflow: "hidden",
                },
                "td:last-of-type": {
                  borderRadius: "0 32px 32px 0",
                  overflow: "hidden",
                },
              }}
              _last={{
                border: "none",
              }}
            >
              <Td>
                <HStack spacing={2}>
                  <Image
                    src={cellarConfig.lpToken.imagePath}
                    alt="lp token image"
                    height="20px"
                  />
                  <Text textAlign="right">
                    {(
                      pendingWithdrawShares /
                      10 ** cellarConfig.cellar.decimals
                    ).toLocaleString()}
                  </Text>
                </HStack>
              </Td>
              <Td>
                <HStack spacing={2}>
                  <Text textAlign="right">
                    {pendingWithdrawSharePrice.toLocaleString()}
                  </Text>
                </HStack>
              </Td>
              <Td>
                <HStack spacing={2}>
                  <Text textAlign="right">
                    {formatTimeRemaining(pendingWithdrawDeadline)}
                  </Text>
                </HStack>
              </Td>
              <Td fontWeight="normal">
                <Flex justify="flex-end">
                  <HStack spacing={2}>
                    <WithdrawQueueButton
                      size="sm"
                      chain={cellarConfig.chain}
                      buttonLabel="Replace Withdraw Request"
                      onSuccessfulWithdraw={setWithdrawRequestData}
                    />
                    <SecondaryButton
                      size="sm"
                      onClick={() => {
                        // Submit Cancel
                        handleCancellation()

                        // Refresh Data
                        setWithdrawRequestData()
                      }}
                    >
                      Cancel Request
                    </SecondaryButton>
                  </HStack>
                </Flex>
              </Td>
            </Tr>
          </Tbody>
        </Table>
      </TableContainer>
    </InnerCard>
  )
}

