  Stack,
  Text,
  Link,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalCloseButton,
  ModalBody,
} from "@chakra-ui/react"
import { useForm } from "react-hook-form"
import { BaseButton } from "components/_buttons/BaseButton"
import { AiOutlineInfo } from "react-icons/ai"
import { useBrandedToast } from "hooks/chakra"
import { useAccount } from "wagmi"
import { toEther } from "utils/formatCurrency"
import { useHandleTransaction } from "hooks/web3"
import { analytics } from "utils/analytics"
import { useRouter } from "next/router"
import { cellarDataMap } from "data/cellarDataMap"
import { useCreateContracts } from "data/hooks/useCreateContracts"
import { useUserBalance } from "data/hooks/useUserBalance"
import { estimateGasLimitWithRetry } from "utils/estimateGasLimit"
import { useGeo } from "context/geoContext"
import { waitTime } from "data/uiConfig"
import { useUserStrategyData } from "data/hooks/useUserStrategyData"
import { useDepositModalStore } from "data/hooks/useDepositModalStore"
import { fetchCellarRedeemableReserves } from "queries/get-cellar-redeemable-asssets"
import { fetchCellarPreviewRedeem } from "queries/get-cellar-preview-redeem"
import { WithdrawQueueButton } from "components/_buttons/WithdrawQueueButton"
import { parseUnits } from "viem"
import {
  handleTransactionError,
  getTransactionErrorToast,
  getTransactionErrorAnalytics,
  type TransactionErrorContext,
} from "utils/handleTransactionError"

interface FormValues {
  withdrawAmount: number
}

interface WithdrawFormProps {
  onClose: () => void
}

export const WithdrawForm = ({ onClose }: WithdrawFormProps) => {
  const {
    register,
    watch,
    handleSubmit,
    setValue,
    formState: { errors, isSubmitting },
  } = useForm<FormValues>()

  const { id: _id } = useDepositModalStore()

  const { addToast, close, closeAll } = useBrandedToast()
  const { address } = useAccount()

  const openWithdrawQueueModal = () =>
    setIsWithdrawQueueModalOpen(true)
  const closeWithdrawQueueModal = () => {
    setIsWithdrawQueueModalOpen(false)
    onClose()
  }

  const id = (useRouter().query.id as string) || _id
  const cellarConfig = cellarDataMap[id].config

  const { refetch } = useUserStrategyData(
    cellarConfig.cellar.address,
    cellarConfig.chain.id
  )

  const { cellarSigner } = useCreateContracts(cellarConfig)

  const [isWithdrawQueueModalOpen, setIsWithdrawQueueModalOpen] =
    useState(false)

  const { lpToken } = useUserBalance(cellarConfig)
  const { data: lpTokenData, isLoading: isBalanceLoading } = lpToken

  const { doHandleTransaction } = useHandleTransaction()

  const watchWithdrawAmount = watch("withdrawAmount")
  const isDisabled =
    isNaN(watchWithdrawAmount) || watchWithdrawAmount <= 0
  const isError = errors.withdrawAmount

  const setMax = () => {
    const amount = parseFloat(
      toEther(lpTokenData?.formatted, lpTokenData?.decimals, false, 6)
    )
    setValue("withdrawAmount", amount)
  }

  useEffect(() => {
    if (watchWithdrawAmount !== null) {
      analytics.track("withdraw.amount-selected", {
        account: address,
        amount: watchWithdrawAmount,
      })
    }
  }, [watchWithdrawAmount, address])

  const geo = useGeo()
  const onSubmit = async ({ withdrawAmount }: FormValues) => {
    if (geo?.isRestrictedAndOpenModal()) {
      return
    }
    if (withdrawAmount <= 0) return

    if (!address) {
      addToast({
        heading: "Withdraw Position",
        status: "default",
        body: <Text>Connect Wallet</Text>,
        closeHandler: close,
        duration: null,
      })

      return
    }

    const analyticsData = {
      account: address,
      amount: withdrawAmount,
    }

    const amtInWei = parseUnits(
      `${withdrawAmount}`,
      cellarConfig.cellar.decimals
    )

    try {
      const gasLimitEstimated = await estimateGasLimitWithRetry(
        cellarSigner?.estimateGas.redeem,
        cellarSigner?.simulate.redeem,
        [amtInWei, address, address],
        330000,
        address
      )

      // @ts-ignore
      const hash = await cellarSigner?.write.redeem(
        [amtInWei, address, address],
        {
          gas: gasLimitEstimated,
          account: address,
        }
      )

      const onSuccess = () => {
        analytics.track("withdraw.succeeded", analyticsData)
        onClose() // Close modal after successful withdraw.
      }

      const onError = (error: Error) => {
        analytics.track("withdraw.failed", {
          ...analyticsData,
          error: error.name,
          message: error.message,
        })
      }

      await doHandleTransaction({
        cellarConfig,
        hash,
        onSuccess,
        onError,
      })
    } catch (e) {
      const error = e as Error

      // Get Redeemable Assets
      const redeemableAssets: number = parseInt(
        await fetchCellarRedeemableReserves(id)
      )

      // previewRedeem on the shares the user is attempting to withdraw
      // Only get previewRedeem on 1 share to optimize caching and do relevant math below
      const previewRedeem: number = parseInt(
        await fetchCellarPreviewRedeem(
          id,
          BigInt(10 ** cellarConfig.cellar.decimals)
        )
      )
      const redeemAmt: number = Math.floor(
        previewRedeem * watchWithdrawAmount
      )
      const redeemingMoreThanAvailible = redeemAmt > redeemableAssets

      /*
      console.log("---")
      console.log("Reedemable assets: ", redeemableAssets)
      console.log("Withdraw amount: ", watchWithdrawAmount)
      console.log("Preview redeem: ", previewRedeem)
      console.log("Redeeming amt: ", redeemAmt)
      console.log("Redeeming more than availible: ", redeemingMoreThanAvailible)
      console.log("---")
      */

      // Check if attempting to withdraw more than availible
      if (redeemingMoreThanAvailible) {
        // Open a modal with information about the withdraw queue
        openWithdrawQueueModal()
      } else {
        // Use centralized error handling
        const errorContext: TransactionErrorContext = {
          vaultName: cellarDataMap[id].name,
          transactionType: "withdraw",
          value: withdrawAmount,
          chainId: cellarConfig.chain.wagmiId,
        }

        const normalizedError = handleTransactionError(
          error,
          errorContext
        )
        const toastConfig = getTransactionErrorToast(
          normalizedError,
          errorContext
        )
        const analyticsData = getTransactionErrorAnalytics(
          normalizedError,
          errorContext
        )

        // Track analytics
        analytics.track("withdraw.rejected", {
          ...analyticsData,
        })

        // Show toast with popup guidance if needed
        const toastBody = toastConfig.showPopupGuidance ? (
          <Text>
            {toastConfig.body}
            <br />
            Enable popups for MetaMask and retry.
          </Text>
        ) : (
          <Text>{toastConfig.body}</Text>
        )

        addToast({
          heading: toastConfig.heading,
          body: toastBody,
          status: toastConfig.status,
          closeHandler: closeAll,
        })

        refetch()
        setValue("withdrawAmount", 0)
      }
    }
  }

  return (
    <>
      <Modal
        isOpen={isWithdrawQueueModalOpen}
        onClose={closeWithdrawQueueModal}
      >
        <ModalOverlay />
        <ModalContent
          p={2}
          w="auto"
          zIndex={401}
          borderWidth={1}
          borderColor="purple.dark"
          borderRadius={12}
          bg="surface.bg"
          fontWeight="semibold"
          _focus={{
            outline: "unset",
            outlineOffset: "unset",
            boxShadow: "unset",
          }}
        >
          <ModalHeader>Transaction not submitted</ModalHeader>
          <ModalCloseButton />
          <ModalBody>
            <VStack spacing={8}>
              <Text textAlign={"center"}>
                You are attempting to withdraw beyond the liquid
                reserve. Please submit a withdraw request via the
                withdraw queue.
              </Text>
              <WithdrawQueueButton
                size="md"
                chain={cellarConfig.chain}
                buttonLabel="Submit Withdraw Request"
              />
            </VStack>
          </ModalBody>
        </ModalContent>
      </Modal>
      <VStack
        as="form"
        spacing={8}
        align="stretch"
        onSubmit={handleSubmit(onSubmit)}
        hidden={isWithdrawQueueModalOpen}
      >
        <FormControl isInvalid={isError as boolean | undefined}>
          <Stack spacing={2}>
            <Text fontWeight="bold" color="neutral.400" fontSize="xs">
              Enter Amount
            </Text>
            <HStack
              backgroundColor="surface.tertiary"
              justifyContent="space-between"
              borderRadius={16}
              px={4}
              py={3}
              height="64px"
            >
              <HStack>
                <Image
                  width="16px"
                  height="16px"
                  src={cellarConfig.lpToken.imagePath}
                  alt="coinlogo"
                />
                <Text fontWeight="semibold">
                  {lpTokenData?.symbol}
                </Text>
              </HStack>
              <VStack spacing={0} align="flex-end">
                <Input
                  id="amount"
                  variant="unstyled"
                  pr="2"
                  type="number"
                  step="any"
                  defaultValue="0.00"
                  placeholder="0.00"
                  fontSize="lg"
                  fontWeight={700}
                  textAlign="right"
                  {...register("withdrawAmount", {
                    onChange: (event) => {
                      let val = event.target.value

                      const decimalPos = val.indexOf(".")

                      if (
                        decimalPos !== -1 &&
                        val.length - decimalPos - 1 >
                          cellarConfig.cellar.decimals
                      ) {
                        val = val.substring(
                          0,
                          decimalPos +
                            cellarConfig.cellar.decimals +
                            1
                        ) // Keep token decimal places as max
                        event.target.value = val
                      }
                    },
                    required: "Enter amount",
                    valueAsNumber: true,
                    validate: {
                      positive: (v) =>
                        v > 0 || "You must submit a positive amount.",
                      balance: (v) =>
                        v <=
                          parseFloat(
                            toEther(
                              lpTokenData?.formatted,
                              lpTokenData?.decimals,
                              false,
                              6
                            )
                          ) || "Insufficient balance",
                    },
                  })}
                />
                <HStack spacing={0} fontSize="10px">
                  {isBalanceLoading ? (
                    <Spinner size="xs" mr="2" />
                  ) : (
                    <>
                      <Text as="span">
                        Available:{" "}
                        {(lpTokenData &&
                          toEther(
                            lpTokenData.value,
                            lpTokenData.decimals,
                            false,
                            6
                          )) ||
                          "--"}
                      </Text>
                      <Button
                        variant="unstyled"
                        p={0}
                        w="max-content"
                        h="max-content"
                        textTransform="uppercase"
                        fontSize="inherit"
                        fontWeight={600}
                        onClick={setMax}
                      >
                        max
                      </Button>
                    </>
                  )}
                </HStack>
              </VStack>
            </HStack>

            <FormErrorMessage color="energyYellow">
              <Icon
                p={0.5}
                mr={1}
                color="surface.bg"
                bg="red.base"
                borderRadius="50%"
                as={AiOutlineInfo}
              />
              {errors.withdrawAmount?.message}
            </FormErrorMessage>
          </Stack>
        </FormControl>
        <Stack>
          <Text
            fontSize="sm"
            fontWeight="semibold"
            color="neutral.400"
          >
            Transaction Details
          </Text>
          <Stack>
            <TransactionDetailItem
              title="Vault"
              value={<Text>{cellarDataMap[id].name}</Text>}
            />
          </Stack>
        </Stack>

        <BaseButton
          type="submit"
          isDisabled={isDisabled}
          isLoading={isSubmitting}
          fontSize={21}
          py={6}
          px={12}
        >
          Submit
        </BaseButton>
        {waitTime(cellarConfig) !== null && (
          <Text textAlign="center">
            Please wait {waitTime(cellarConfig)} after the deposit to
            Withdraw
          </Text>
        )}
      </VStack>
    </>
  )
}

const TransactionDetailItem = ({
  title,
  value,
}: {
  title: string
  value: React.ReactNode
}) => {
  return (
    <HStack alignItems="flex-start" justifyContent="space-between">
      <Text color="neutral.300">{title}</Text>
      <Stack>{value}</Stack>
    </HStack>
  )
}

import React, { useState, useEffect } from "react"
import {
  FormControl,
  FormErrorMessage,
  FormLabel,
  FormHelperText,
  Icon,
  VStack,
  Button,
  HStack,
  Input,
  Spinner,
  Image,
  Stack,
  Text,
  Link,
  Tooltip,
  InputRightElement,
  InputGroup,
} from "@chakra-ui/react"
import { FormProvider, useForm } from "react-hook-form"
import { BaseButton } from "components/_buttons/BaseButton"
import { AiOutlineInfo } from "react-icons/ai"
import { useBrandedToast } from "hooks/chakra"
import { useAccount, usePublicClient, useWalletClient } from "wagmi"
import { getContract, parseUnits } from "viem"
import { toEther } from "utils/formatCurrency"
import { useHandleTransaction } from "hooks/web3"
import { useRouter } from "next/router"
import { cellarDataMap } from "data/cellarDataMap"
import { useUserBalance } from "data/hooks/useUserBalance"
import { estimateGasLimitWithRetry } from "utils/estimateGasLimit"
import { useGeo } from "context/geoContext"
import { useUserStrategyData } from "data/hooks/useUserStrategyData"
import { useDepositModalStore } from "data/hooks/useDepositModalStore"
import { Token } from "data/tokenConfig"
import { ModalOnlyTokenMenu } from "components/_menus/ModalMenu"
import { InformationIcon } from "components/_icons"
import { FAQAccordion } from "components/_cards/StrategyBreakdownCard/FAQAccordion"
import withdrawQueueV0821 from "src/abi/withdraw-queue-v0.8.21.json"
import { fetchCellarPreviewRedeem } from "queries/get-cellar-preview-redeem"
import { WITHDRAW_DEADLINE_HOURS } from "src/constants/withdraw"
import { getAddress } from "viem"
import { useWaitForTransaction } from "hooks/wagmi-helper/useWaitForTransactions"
import { MaxUint256 } from "utils/bigIntHelpers"
import { useCreateContracts } from "data/hooks/useCreateContracts"
import { useBoringQueueWithdrawals } from "data/hooks/useBoringQueueWithdrawals"
import { useWithdrawRequestStatus } from "data/hooks/useWithdrawRequestStatus"
import { logTxDebug } from "utils/txDebug"

interface FormValues {
  withdrawAmount: number
}

interface WithdrawQueueFormProps {
  onClose: () => void
  onSuccessfulWithdraw?: () => void
}

const DEADLINE_HOURS = 288
const SHARE_PRICE_DISCOUNT_PERCENT = 0.25

export const WithdrawQueueForm = ({
  onClose,
  onSuccessfulWithdraw,
}: WithdrawQueueFormProps) => {
  const {
    register,
    watch,
    handleSubmit,
    setValue,
    formState: { errors, isSubmitting },
  } = useForm<FormValues>()

  const { id: _id } = useDepositModalStore()

  const { addToast, update, close, closeAll } = useBrandedToast()
  const { address } = useAccount()

  const id = (useRouter().query.id as string) || _id
  const cellarConfig = cellarDataMap[id].config

  const { refetch } = useUserStrategyData(
    cellarConfig.cellar.address,
    cellarConfig.chain.id
  )

  const { data: boringQueueWithdrawals } = useBoringQueueWithdrawals(
    cellarConfig.cellar.address,
    cellarConfig.chain.id,
    { enabled: !!cellarConfig.boringQueue }
  )

  const { data: walletClient } = useWalletClient()
  const publicClient = usePublicClient()

  const { boringQueue } = useCreateContracts(cellarConfig)

  const cellarContract = (() => {
    if (!publicClient) return
    return getContract({
      address: cellarConfig.cellar.address as `0x${string}`,
      abi: cellarConfig.cellar.abi,
      client: {
        public: publicClient,
        wallet: walletClient,
      },
    })
  })()

  const withdrawQueueContract = (() => {
    if (!publicClient) return
    return (
      boringQueue ??
      getContract({
        address: cellarConfig.chain
          .withdrawQueueAddress as `0x${string}`,
        abi: withdrawQueueV0821,
        client: {
          public: publicClient,
          wallet: walletClient,
        },
      })
    )
  })()

  const [_, wait] = useWaitForTransaction({
    skip: true,
  })

  const { lpToken } = useUserBalance(cellarConfig)
  const { data: lpTokenData, isLoading: isBalanceLoading } = lpToken
  let strategyBaseAsset: Token = cellarConfig.baseAsset

  const modalFormMethods = useForm<FormValues>({
    defaultValues: {},
  })

  const [selectedToken, setSelectedToken] =
    useState<Token>(strategyBaseAsset)

  const [isWithdrawAllowed, setIsWithdrawAllowed] = useState<boolean | null>(
    null
  )

  // Preflight: check if queue allows withdraws for selected asset (boring queue)
  useEffect(() => {
    let cancelled = false
    const run = async () => {
      try {
        if (!boringQueue || !selectedToken?.address) {
          setIsWithdrawAllowed(null)
          return
        }
        const res = await boringQueue.read.withdrawAssets([
          selectedToken.address,
        ])
        const allow = Array.isArray(res) ? Boolean(res[0]) : Boolean(res)
        if (!cancelled) setIsWithdrawAllowed(allow)
        logTxDebug("withdraw.preflight", {
          assetOut: selectedToken.address,
          allow,
        })
      } catch (e) {
        if (!cancelled) setIsWithdrawAllowed(null)
      }
    }
    run()
    return () => {
      cancelled = true
    }
  }, [boringQueue, selectedToken])

  function trackedSetSelectedToken(value: Token) {
    if (value && value !== selectedToken) {
      // analytics.track("deposit.stable-selected", {
      //   ...baseAnalytics,
      //   stable: value.symbol,
      // })
    }

    setSelectedToken(value)
  }

  const { doHandleTransaction } = useHandleTransaction()

  const watchWithdrawAmount = watch("withdrawAmount")

  const isDisabled =
    isNaN(watchWithdrawAmount) || watchWithdrawAmount <= 0

  const isActiveWithdrawRequest =
    useWithdrawRequestStatus(cellarConfig)

  const setMax = () => {
    const amount = parseFloat(
      toEther(lpTokenData?.formatted, lpTokenData?.decimals, false, 6)
    )
    setValue("withdrawAmount", amount)
  }

  const geo = useGeo()
  const onSubmit = async ({ withdrawAmount }: FormValues) => {
    if (geo?.isRestrictedAndOpenModal()) {
      return
    }
    if (withdrawAmount <= 0) return

    if (!address) {
      addToast({
        heading: "Withdraw Queue",
        status: "default",
        body: <Text>Connect Wallet</Text>,
        closeHandler: close,
        duration: null,
      })

      return
    }

    const withdrawAmtInBaseDenom = parseUnits(
      `${withdrawAmount}`,
      cellarConfig.cellar.decimals
    )

    // Check if user has sufficient balance
    if (!lpTokenData || lpTokenData.value < withdrawAmtInBaseDenom) {
      addToast({
        heading: "Insufficient Balance",
        status: "error",
        body: (
          <Text>
            You don&apos;t have enough{" "}
            {lpTokenData?.symbol || "tokens"} to withdraw this amount.
            <br />
            Available: {lpTokenData?.formatted || "0"}
            <br />
            Requested: {withdrawAmount}
          </Text>
        ),
        closeHandler: closeAll,
      })
      return
    }

    const allowance = (await cellarContract?.read.allowance([
      address!,
      getAddress(
        cellarConfig.boringQueue
          ? cellarConfig.boringQueue.address
          : cellarConfig.chain.withdrawQueueAddress
      ),
    ])) as bigint

    let needsApproval
    try {
      needsApproval = (allowance as bigint) < withdrawAmtInBaseDenom
    } catch (e) {
      const error = e as Error
      console.error("Invalid Input: ", error.message)
      return
    }

    if (needsApproval) {
      try {
        // @ts-ignore
        const hash = await cellarContract?.write.approve(
          [
            getAddress(
              cellarConfig.boringQueue
                ? cellarConfig.boringQueue.address
                : cellarConfig.chain.withdrawQueueAddress
            ),
            MaxUint256,
          ],
          { account: address }
        )
        addToast({
          heading: "ERC20 Approval",
          status: "default",
          body: <Text>Approving ERC20</Text>,
          isLoading: true,
          closeHandler: close,
          duration: null,
        })
        const waitForApproval = wait({ confirmations: 1, hash })
        const result = await waitForApproval
        if (result?.data?.transactionHash) {
          // analytics.track("deposit.approval-granted", {
          //   ...baseAnalytics,
          //   stable: tokenSymbol,
          //   value: depositAmount,
          // })

          update({
            heading: "ERC20 Approval",
            body: <Text>ERC20 Approved</Text>,
            status: "success",
            closeHandler: closeAll,
          })
        } else if (result?.error) {
          // analytics.track("deposit.approval-failed", {
          //   ...baseAnalytics,
          //   stable: tokenSymbol,
          //   value: depositAmount,
          // })

          update({
            heading: "ERC20 Approval",
            body: <Text>Approval Failed</Text>,
            status: "error",
            closeHandler: closeAll,
          })
        }
      } catch (e) {
        const error = e as Error
        console.error(error.message)
        // analytics.track("deposit.approval-cancelled", {
        //   ...baseAnalytics,
        //   stable: tokenSymbol,
        //   value: depositAmount,
        // })

        addToast({
          heading: "ERC20 Approval",
          body: <Text>Approval Cancelled</Text>,
          status: "error",
          closeHandler: closeAll,
        })
      }
    }

    try {
      // Add debugging information
      console.log("Withdraw attempt details:", {
        selectedToken: selectedToken?.symbol,
        withdrawAmount: withdrawAmount,
        withdrawAmtInBaseDenom: withdrawAmtInBaseDenom.toString(),
        lpTokenBalance: lpTokenData?.formatted,
        lpTokenValue: lpTokenData?.value?.toString(),
        isActiveWithdrawRequest,
        boringQueue: !!boringQueue,
        cellarAddress: cellarConfig.cellar.address,
      })

      // Guard: if boring queue and asset disabled, show UI and stop
      if (boringQueue && isWithdrawAllowed === false) {
        addToast({
          heading: "Withdraw Queue",
          status: "info",
          body: (
            <Text>
              Withdraw queue is currently not available for {selectedToken.symbol}.
              Please choose a different asset or try again later.
            </Text>
          ),
          closeHandler: closeAll,
        })
        logTxDebug("withdraw.blocked_asset", {
          assetOut: selectedToken.address,
        })
        return
      }

      let hash = await doWithdrawTx(
        selectedToken,
        withdrawAmtInBaseDenom
      )

      const onSuccess = () => {
        if (onSuccessfulWithdraw) {
          onSuccessfulWithdraw()
        }

        onClose() // Close modal after successful withdraw.
      }

      const onError = (error: Error) => {
        // Can track here if we want
      }

      await doHandleTransaction({
        cellarConfig,
        hash,
        onSuccess,
        onError,
      })
    } catch (e) {
      const error = e as Error
      console.error(error)

      if (error.message === "GAS_LIMIT_ERROR") {
        const causeMsg = (error as any)?.cause?.message || ""
        if (causeMsg.includes("BoringOnChainQueue__WithdrawsNotAllowedForAsset")) {
          addToast({
            heading: "Withdraw Queue",
            body: (
              <Text>
                Withdraw queue is currently not available for {selectedToken.symbol}.
                Please choose a different asset or try again later.
              </Text>
            ),
            status: "info",
            closeHandler: closeAll,
          })
          logTxDebug("withdraw.error_not_allowed", {
            assetOut: selectedToken.address,
          })
          return
        }
        addToast({
          heading: "Transaction not submitted",
          body: (
            <Text>
              Your transaction has failed, if it does not work after
              waiting some time and retrying please send a message in
              our{" "}
              {
                <Link
                  href="https://discord.com/channels/814266181267619840/814279703622844426"
                  isExternal
                  textDecoration="underline"
                >
                  Discord Support channel
                </Link>
              }{" "}
              tagging a member of the front end team.
            </Text>
          ),
          status: "info",
          closeHandler: closeAll,
        })
      } else {
        console.error(error)
        addToast({
          heading: "Withdraw Queue",
          body: <Text>Withdraw Queue Order Cancelled</Text>,
          status: "error",
          closeHandler: closeAll,
        })
      }

      refetch()
    }
  }

  const doWithdrawTx = async (
    selectedToken: Token,
    withdrawAmtInBaseDenom: bigint
  ) => {
    const currentTime = Math.floor(Date.now() / 1000)
    const deadlineSeconds =
      Math.floor(DEADLINE_HOURS * 60 * 60) + currentTime

    let hash

    if (boringQueue) {
      const discount = BigInt(
        Math.floor(SHARE_PRICE_DISCOUNT_PERCENT * 10000)
      )

      const deadlineSeconds = BigInt(DEADLINE_HOURS * 60 * 60)

      if (isActiveWithdrawRequest && boringQueueWithdrawals) {
        // Replace existing BoringQueuerequest
        const request =
          boringQueueWithdrawals.open_requests[0].metadata

        const oldRequestTouple = [
          request.nonce,
          address,
          request.assetOut,
          request.amountOfShares,
          request.amountOfAssets,
          request.creationTime,
          request.secondsToMaturity,
          request.secondsToDeadline,
        ]

        const gasLimitEstimated = await estimateGasLimitWithRetry(
          boringQueue.estimateGas.replaceOnChainWithdraw,
          boringQueue.simulate.replaceOnChainWithdraw,
          [oldRequestTouple, discount, deadlineSeconds],
          330000,
          address
        )

        // @ts-ignore
        hash = await boringQueue?.write.replaceOnChainWithdraw(
          [oldRequestTouple, discount, deadlineSeconds],
          {
            gas: gasLimitEstimated,
            account: address,
          }
        )
      } else {
        // Create new BoringQueue request
        const gasLimitEstimated = await estimateGasLimitWithRetry(
          boringQueue.estimateGas.requestOnChainWithdraw,
          boringQueue.simulate.requestOnChainWithdraw,
          [
            selectedToken?.address,
            withdrawAmtInBaseDenom,
            discount,
            deadlineSeconds,
          ],
          330000,
          address
        )

        // @ts-ignore
        hash = await boringQueue?.write.requestOnChainWithdraw(
          [
            selectedToken?.address,
            withdrawAmtInBaseDenom,
            discount,
            deadlineSeconds,
          ],
          {
            gas: gasLimitEstimated,
            account: address,
          }
        )
      }
    } else {
      // Create or replace WithdrawQueue request
      const previewRedeem = parseInt(
        await fetchCellarPreviewRedeem(
          id,
          BigInt(10 ** cellarConfig.cellar.decimals)
        )
      )

      const sharePriceStandardized =
        previewRedeem / 10 ** cellarConfig.baseAsset.decimals
      const sharePriceWithDiscount = sharePriceStandardized
      const sharePriceWithDiscountInBaseDenom = Math.floor(
        sharePriceWithDiscount * 10 ** cellarConfig.baseAsset.decimals
      )

      const withdrawTouple = [
        BigInt(deadlineSeconds),
        BigInt(sharePriceWithDiscountInBaseDenom),
        withdrawAmtInBaseDenom,
        false,
      ]
      const gasLimitEstimated = await estimateGasLimitWithRetry(
        withdrawQueueContract?.estimateGas.updateWithdrawRequest,
        withdrawQueueContract?.simulate.updateWithdrawRequest,
        [cellarConfig.cellar.address, withdrawTouple],
        330000,
        address
      )
      // @ts-ignore
      hash = await withdrawQueueContract?.write.updateWithdrawRequest(
        [cellarConfig.cellar.address, withdrawTouple],
        {
          gas: gasLimitEstimated,
          account: address,
        }
      )
    }

    return hash
  }

  return (
    <VStack
      as="form"
      spacing={8}
      align="stretch"
      onSubmit={handleSubmit(onSubmit)}
    >
      <FormProvider {...modalFormMethods}>
        <FormControl isInvalid={!!errors.withdrawAmount}>
          {isActiveWithdrawRequest && (
            <>
              <HStack
                p={4}
                mb={boringQueue ? 0 : 12}
                spacing={4}
                align="flex-start"
                backgroundColor="purple.dark"
                border="2px solid"
                borderRadius={16}
                borderColor="purple.base"
              >
                <Text
                  color="white"
                  fontSize="s"
                  textAlign={"center"}
                  fontWeight={"bold"}
                >
                  You currently have a withdraw request pending in the
                  queue, submitting a new withdraw request will
                  replace your current one.
                </Text>
              </HStack>
              <br />
            </>
          )}
          {isActiveWithdrawRequest && boringQueue && (
            <>
              <HStack
                p={4}
                mb={12}
                spacing={4}
                align="flex-start"
                backgroundColor="purple.dark"
                border="2px solid"
                borderRadius={16}
                borderColor="purple.base"
              >
                <Text
                  color="white"
                  fontSize="s"
                  textAlign={"center"}
                  fontWeight={"bold"}
                >
                  When replacing a BoringQueue request, only the
                  deadline is updated.
                </Text>
              </HStack>
              <br />
            </>
          )}
          <Stack spacing={5}>
            <Text fontWeight="bold" color="neutral.400" fontSize="xs">
              Enter Shares
            </Text>
            <HStack
              backgroundColor="surface.tertiary"
              justifyContent="space-between"
              borderRadius={16}
              px={4}
              py={3}
              height="64px"
            >
              <HStack>
                <Image
                  width="16px"
                  height="16px"
                  src={cellarConfig.lpToken.imagePath}
                  alt="coinlogo"
                />
                <Text fontWeight="semibold">
                  {lpTokenData?.symbol}
                </Text>
              </HStack>
              <VStack spacing={0} align="flex-end">
                <Input
                  id="amount"
                  variant="unstyled"
                  pr="2"
                  type="number"
                  step="any"
                  defaultValue="0.00"
                  placeholder="0.00"
                  fontSize="lg"
                  fontWeight={700}
                  disabled={isActiveWithdrawRequest && !!boringQueue}
                  textAlign="right"
                  {...register("withdrawAmount", {
                    onChange: (event) => {
                      let val = event.target.value

                      const decimalPos = val.indexOf(".")

                      if (
                        decimalPos !== -1 &&
                        val.length - decimalPos - 1 >
                          cellarConfig.cellar.decimals
                      ) {
                        val = val.substring(
                          0,
                          decimalPos +
                            cellarConfig.cellar.decimals +
                            1
                        ) // Keep token decimal places as max
                        event.target.value = val
                      }
                    },
                    required: "Enter amount",
                    valueAsNumber: true,
                    validate: {
                      positive: (v) =>
                        v > 0 || "You must submit a positive amount.",
                      balance: (v) =>
                        v <=
                          parseFloat(
                            toEther(
                              lpTokenData?.formatted,
                              lpTokenData?.decimals,
                              false,
                              6
                            )
                          ) || "Insufficient balance",
                    },
                  })}
                />
                <HStack spacing={0} fontSize="10px">
                  {isBalanceLoading ? (
                    <Spinner size="xs" mr="2" />
                  ) : (
                    <>
                      <Text as="span">
                        Available:{" "}
                        {(lpTokenData &&
                          toEther(
                            lpTokenData.value,
                            lpTokenData.decimals,
                            false,
                            6
                          )) ||
                          "--"}
                      </Text>
                      <Button
                        variant="unstyled"
                        p={0}
                        w="max-content"
                        h="max-content"
                        textTransform="uppercase"
                        fontSize="inherit"
                        fontWeight={600}
                        onClick={setMax}
                        disabled={
                          isActiveWithdrawRequest && !!boringQueue
                        }
                      >
                        max
                      </Button>
                    </>
                  )}
                </HStack>
              </VStack>
            </HStack>

            <FormErrorMessage color="energyYellow">
              <Icon
                p={0.5}
                mr={1}
                color="surface.bg"
                bg="red.base"
                borderRadius="50%"
                as={AiOutlineInfo}
              />
              {errors.withdrawAmount?.message}
            </FormErrorMessage>
            <HStack justify="space-between">
              <Text as="span">Asset Out</Text>
              {
                <ModalOnlyTokenMenu
                  depositTokens={
                    cellarConfig.boringVault
                      ? Object.keys(
                          cellarDataMap[id].config
                            .withdrawTokenConfig!
                        )
                      : [strategyBaseAsset.symbol]
                  }
                  activeAsset={strategyBaseAsset.address}
                  setSelectedToken={trackedSetSelectedToken}
                  isDisabled={
                    isActiveWithdrawRequest && !!boringQueue
                  }
                />
              }
            </HStack>
          </Stack>
        </FormControl>
        <Stack spacing={5}>
          <Text
            fontSize="xs"
            fontWeight="semibold"
            color="neutral.400"
          >
            Transaction Details
          </Text>
          <Stack spacing={2.5}>
            <TransactionDetailItem
              title="Vault"
              value={<Text>{cellarDataMap[id].name}</Text>}
            />
          </Stack>
        </Stack>
      </FormProvider>

      <BaseButton
        type="submit"
        isDisabled={
          isDisabled || (boringQueue ? isWithdrawAllowed === false : false)
        }
        isLoading={isSubmitting}
        fontSize={21}
        py={6}
        px={12}
      >
        Submit
      </BaseButton>
      {boringQueue && isWithdrawAllowed === false && (
        <Text color="yellow.300" fontSize="sm">
          Withdraw queue is currently not available for {selectedToken.symbol}.
        </Text>
      )}
