export interface MenuProps
  extends Omit<ModalMenuProps, "setSelectedToken"> {
  value: Token
  onChange: (...events: any[]) => void
}

export const Menu = ({
  depositTokens,
  activeAsset,
  selectedTokenBalance,
  value,
  onChange,
  isDisabled,
}: MenuProps) => {
  const { colors } = useTheme()
  const menuRef = useRef(null)
  const { width } = useSize(menuRef) ?? { width: 0 }
  const { register, setValue, clearErrors, watch } = useFormContext()
  const availableBalance = `${toEther(
    selectedTokenBalance?.value,
    selectedTokenBalance?.decimals,
    false,
    6
  )}`
  const { id: _id } = useDepositModalStore()
  const id = (useRouter().query.id as string) || _id
  const cellarData = cellarDataMap[id]
  const cellarConfig = cellarData.config

  const rawDepositAmount = watch("depositAmount")
  const depositTokenConfig = getTokenConfig(
    depositTokens,
    cellarConfig.chain.id
  ) as Token[]

  // Get the default deposit asset for this cellar
  const defaultAssetSymbol = depositAssetDefaultValue(cellarConfig)
  const defaultToken =
    depositTokenConfig.find(
      (token) => token.symbol === defaultAssetSymbol
    ) || depositTokenConfig[0]

  const [selectedToken, setSelectedToken] = useState<
    Token | undefined
  >(defaultToken)

  const setMax = () => {
    // analytics.track("deposit.max-selected", {
    //   value: selectedTokenBalance?.value?.toString(),
    // })

    return setValue(
      "depositAmount",
      parseFloat(
        toEther(
          selectedTokenBalance?.value,
          selectedTokenBalance?.decimals,
          false,
          6
        )
      )
    )
  }
  const [displayedBalance, setDisplayedBalance] = useState(0)
  useEffect(() => {
    const fetchAndUpdateBalance = async () => {
      if (rawDepositAmount) {
        try {
          const price = await fetchCoingeckoPrice(
            selectedToken!,
            "usd"
          )
          const newBalance = rawDepositAmount * Number(price || 0)
          setDisplayedBalance(newBalance)
        } catch (error) {
          console.error("Error fetching price:", error)
        }
      } else {
        setDisplayedBalance(0)
      }
    }

    fetchAndUpdateBalance()
  }, [rawDepositAmount])

  const [searchTerm, setSearchTerm] = useState("")

  // Function to handle search input changes
  const handleSearchChange = (
    event: ChangeEvent<HTMLInputElement>
  ) => {
    setSearchTerm(event.target.value.toLowerCase())
  }

  // Filter tokens based on search term
  const filteredTokens = depositTokenConfig.filter((token) =>
    token.symbol.toLowerCase().includes(searchTerm)
  )

  return (
    <HStack
      ref={menuRef}
      p={4}
      justifyContent="space-between"
      w="100%"
      bg="surface.secondary"
      border="none"
      borderRadius={16}
      appearance="none"
      textAlign="start"
      _first={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
      }}
    >
      <VStack w="100%" spacing={0} align="flex-start">
        {/* @ts-ignore using string where number is expected. This is to ensure popover is always placed at the top of button, no matter the height value. */}
        <ChMenu offset={["10%", "100%"]} placement="bottom">
          <MenuButton
            as={Box}
            border="none"
            borderRadius={16}
            appearance="none"
            fontSize="lg"
            fontWeight={700}
          >
            <HStack>
              {value ? (
                <HStack spacing={2}>
                  <Image
                    boxSize={6}
                    src={value.src}
                    alt={value.alt}
                    style={{ borderRadius: "50%" }}
                  />
                  <span>{value.symbol}</span>
                </HStack>
              ) : (
                <Text as="span">Select Token</Text>
              )}
              {depositTokens.length > 1 && (
                <Icon as={FaChevronDown} />
              )}
            </HStack>
          </MenuButton>
          <MenuList
            bg="surface.bg"
            borderColor="purple.base"
            borderRadius={16}
            zIndex="overlay"
            boxShadow={`0 2px 24px 0 ${colors.surface.tertiary}`}
            w={width}
            maxH="30em"
            overflowY="auto"
            scrollBehavior="smooth"
          >
            <Box pt={4} pb={2} pl={10} width="90%">
              <Input
                placeholder="Select Deposit Asset"
                onChange={handleSearchChange}
                value={searchTerm}
                isDisabled={isDisabled ?? false}
              />
            </Box>
            <MenuOptionGroup
              defaultValue={depositTokenConfig[0].symbol}
              type="radio"
            >
              {" "}
              {/*
              <Box pt={4} pb={2} pl={10}>
                <Text color="neutral.400">Select deposit asset</Text>
              </Box>*/}
              {filteredTokens.map((token) => {
                const { address, src, alt, symbol } = token
                const isActiveAsset =
                  token.address.toUpperCase() ===
                  activeAsset?.toUpperCase()

                const isCellerDepositAsset =
                  (cellarData.depositTokens.list.includes(
                    token.symbol.toUpperCase()
                  ) ||
                    cellarData.depositTokens.list.includes(
                      token.symbol
                    )) &&
                  !isActiveAsset

                // Set default selected token to active asset.
                if (isActiveAsset && !value) onChange(token)

                return (
                  <MenuItemOption
                    key={address}
                    value={symbol}
                    borderRadius={8}
                    _hover={{ bg: "rgba(96, 80, 155, 0.4)" }}
                    isDisabled={isDisabled ?? false}
                    onClick={() => {
                      clearErrors()
                      onChange(token)
                      setSelectedToken(token)
                      setDisplayedBalance(0)
                      setValue("depositAmount", 0)
                    }}
                  >
                    <HStack justify="space-between">
                      <HStack width="100%">
                        <Image
                          boxSize={6}
                          src={src}
                          alt={alt}
                          style={{ borderRadius: "50%" }}
                        />
                        <span>{symbol}</span>
                        {isActiveAsset && (
                          <HStack
                            justifyItems={"right"}
                            width="100%"
                            justifyContent="flex-end"
                            alignItems="flex-start"
                            p={3}
                          >
                            <ActiveAssetIcon
                              boxSize={5}
                              alignSelf="center"
                            />
                            <Text fontSize="xs" fontWeight={600}>
                              Accounting Asset
                            </Text>
                          </HStack>
                        )}
                        {isCellerDepositAsset && (
                          <HStack
                            justifyItems={"right"}
                            width="100%"
                            justifyContent="flex-end"
                            alignItems="flex-start"
                            p={3}
                          >
                            <CellarGradientIcon
                              boxSize={3}
                              alignSelf="center"
                            />
                            <Text fontSize="xs" fontWeight={600}>
                              Alt Deposit Asset
                            </Text>
                          </HStack>
                        )}
                      </HStack>
                    </HStack>
                  </MenuItemOption>
                )
              })}
            </MenuOptionGroup>
          </MenuList>
        </ChMenu>
        <HStack spacing={0} fontSize="10px" paddingTop="0.75em">
          <Text as="span">Available: {availableBalance}</Text>
          <Button
            variant="unstyled"
            p={0}
            w="max-content"
            h="max-content"
            textTransform="uppercase"
            onClick={setMax}
            fontSize="inherit"
            fontWeight={600}
            isDisabled={isDisabled ?? false}
          >
            max
          </Button>
        </HStack>
      </VStack>
      <VStack spacing={0} align="flex-end">
        <Input
          variant="unstyled"
          pr="2"
          type="number"
          step="any"
          defaultValue="0.00"
          placeholder="0.00"
          fontSize="lg"
          fontWeight={700}
          textAlign="right"
          width="100%"
          isDisabled={isDisabled ?? false}
          {...register("depositAmount", {
            onChange: (event) => {
              if (event && event.target) {
                // analytics.track("deposit.amount-selected", {
                //   value: event.target.value,
                // })
              }
              let val = event.target.value

              const decimalPos = val.indexOf(".")

              if (
                decimalPos !== -1 &&
                val.length - decimalPos - 1 > value.decimals
              ) {
                val = val.substring(
                  0,
                  decimalPos + value.decimals + 1
                ) // Keep token decimal places as max
                event.target.value = val
              }
            },
            required: "Enter amount",
            valueAsNumber: true,
            validate: {
              positive: (v) =>
                v > 0 || "You must submit a positive amount.",
              lessThanBalance: (v) => {
                return (
                  v <= parseFloat(availableBalance) ||
                  "Insufficient balance"
                )
              },
              // depositLessThanFifty: (v) =>
              //   v <= 50000 ||
              //   "You cannot exceed the cellar limit of $50,000.",
              // depositLimit: (v) => {
              //   if (!depositData?.wallet) return true

              //   const currentDeposits = parseFloat(
              //     toEther(
              //       depositData?.wallet?.currentDeposits!,
              //       18,
              //       false
              //     )
              //   )
              //   const sum = v + currentDeposits

              //   return (
              //     sum <= 50000 ||
              //     `You cannot exceed the cellar limit of $50,000. You currently have $${currentDeposits} deposited in this cellar.`
              //   )
              // },
            },
          })}
        />
        <HStack spacing={0} fontSize="11px" textAlign="right" pr="2">
          <Text as="span">
            $ {Number(displayedBalance.toFixed(2)).toLocaleString()}
          </Text>
        </HStack>
      </VStack>
    </HStack>
  )
}

