import React, { useMemo } from "react"
import { Button, Stack, Text, VisuallyHidden } from "@chakra-ui/react"
import { useAccount } from "wagmi"
import { useBrandedToast } from "hooks/chakra"
import { chainConfig, Chain } from "data/chainConfig"
import { requestSwitchWithAdd } from "utils/wallet/chainUtils"

interface NetworkSelectorProps {
  requiredChainId?: number
  mode?: "inline" | "button"
  onSwitched?: (chainId: number) => void
  fullWidth?: boolean
}

export default function NetworkSelector({
  requiredChainId,
  mode = "button",
  onSwitched,
  fullWidth = false,
}: NetworkSelectorProps) {
  const { chain } = useAccount()
  const { addToast } = useBrandedToast()

  const currentChain: Chain | undefined = useMemo(() => {
    if (!chain?.id) return undefined
    return chainConfig.find((c) => c.wagmiId === chain.id)
  }, [chain?.id])

  const requiredChain: Chain | undefined = useMemo(() => {
    if (!requiredChainId) return undefined
    return chainConfig.find((c) => c.wagmiId === requiredChainId)
  }, [requiredChainId])

  const handleQuickSwitch = async () => {
    if (!requiredChain) return
    try {
      await requestSwitchWithAdd(requiredChain.wagmiId as 1 | 42161 | 8453)
      onSwitched?.(requiredChain.wagmiId)
    } catch (e: any) {
      addToast({
        heading: "Network switch failed",
        status: "error",
        body: <Text>{e?.message ?? "Unable to switch network"}</Text>,
      })
    }
  }

  const caption =
    currentChain && requiredChain
      ? `You're on ${currentChain.displayName}. This vault runs on ${requiredChain.displayName}.`
      : undefined

  return (
    <Stack spacing={2} w={fullWidth ? "100%" : "auto"}>
      <VisuallyHidden aria-live="polite">
        Select or switch network
      </VisuallyHidden>
      {requiredChain &&
        currentChain?.wagmiId !== requiredChain.wagmiId && (
          <Button
            onClick={handleQuickSwitch}
            size={mode === "inline" ? "lg" : "md"}
            w={fullWidth ? "100%" : undefined}
            height={"44px"}
            _focusVisible={{
              boxShadow: "0 0 0 3px var(--chakra-colors-purple-base)",
            }}
            aria-describedby={
              caption ? "network-switch-caption" : undefined
            }
          >
            {`Switch to ${requiredChain.displayName}`}
          </Button>
        )}
      {caption && (
        <Text
          id="network-switch-caption"
          fontSize="sm"
          color="gray.500"
          textAlign="center"
        >
          {caption}
        </Text>
      )}
    </Stack>
  )
}

import { createContext, FC, ReactNode, useContext, useState } from "react"

export interface DialogContext {
  title?: string
  body?: any
  isOpen?: boolean
  openDialog: (title?: string, body?: any) => void
  onClose: () => void
}

const defaultContext: DialogContext = {
  title: '',
  body: '',
  onClose: () => {},
  openDialog: () => {}
}

export const dialogContext = createContext<DialogContext>(defaultContext)

export const DialogProvider: FC<{ children: ReactNode; }> = ({ children }) => {
  const [isOpen, setIsOpen] = useState<boolean>(false)
  const [title, setTitle] = useState<string>()
  const [body, setBody] = useState<string>()

  const openDialog = (title?: string, body?: any) => {
    setIsOpen(true)
    setTitle(title)
    if (body) setBody(body)
  }

  const onClose = () => setIsOpen(false)

  const value = {
    title,
    body,
    isOpen,
    openDialog,
    onClose
  }

  return (
    <dialogContext.Provider value={value}>{children}</dialogContext.Provider>
  )
}

export const useDialog = () => useContext(dialogContext)

import { Chain } from "src/data/chainConfig"
import { createPublicClient, getAddress, getContract, PublicClient } from "viem"
import { http } from "wagmi"

export const ALCHEMY_API_KEY = process.env.NEXT_PUBLIC_ALCHEMY_KEY
export const INFURA_API_KEY = process.env.NEXT_PUBLIC_INFURA_API_KEY
export const QUICKNODE_API_KEY = process.env.NEXT_PUBLIC_QUICKNODE_KEY

export const ALCHEMY_API_URL = {
  ethereum: "https://eth-mainnet.alchemyapi.io/v2",
  arbitrum: "https://arb-mainnet.alchemyapi.io/v2",
  optimism: "https://opt-mainnet.alchemyapi.io/v2"
}
export const INFURA_API_URL = {
  ethereum: "https://mainnet.infura.io/v3",
  arbitrum: "https://arbitrum-mainnet.infura.io/v3",
  optimism: "https://optimism-mainnet.infura.io/v3"
}

export const QUICKNODE_API_URL = {
  scroll: "https://damp-cool-model.scroll-mainnet.quiknode.pro/"
}
export async function getActiveProvider(chain: Chain) {
  let publicClient: PublicClient | null = null

  if (chain.quicknodeRpcUrl && QUICKNODE_API_KEY) {
    publicClient = createPublicClient({
      chain: chain.viemChain,
      transport: http(`${chain.quicknodeRpcUrl}/${QUICKNODE_API_KEY}`)
    })
    console.log("Attempting to connect via Quicknode...")
  } else if (chain.alchemyRpcUrl && ALCHEMY_API_KEY) {
    publicClient = createPublicClient({
      chain: chain.viemChain,
      transport: http(`${chain.alchemyRpcUrl}/${ALCHEMY_API_KEY}`)
    })
    console.log("Attempting to connect via Alchemy...")
  } else if (chain.infuraRpcUrl && INFURA_API_KEY) {
    publicClient = createPublicClient({
      chain: chain.viemChain,
      transport: http(`${chain.infuraRpcUrl}/${INFURA_API_KEY}`)
    })

    console.log("Attempting to connect via Infura...")
  }

  // Attempt to connect using the configured provider
  try {
    await publicClient?.getBlockNumber()
    console.log("Connected to provider")
    return publicClient
  } catch (error) {
    console.error("Failed to connect to any provider!", error)
    return null
  }
}

export async function queryContract(
  contractAddress: string,
  abi: readonly {}[],
  chain: Chain
) {
  const publicClient = await getActiveProvider(chain)

  if (!publicClient) {
    console.error("No provider is available!")
    return null
  }

  const contract = getContract({
    abi,
    address: getAddress(contractAddress),
    client: publicClient
  })
  return contract
}

