import { zonedTimeToUtc } from "date-fns-tz"

export const getStakingEnd = async (
  stakerContract: any
) => {
  try {
    const ended = await stakerContract.read.ended()
    const endTimestamp = await stakerContract.read.endTimestamp()
    const endDate =
      Number(endTimestamp) === 0
        ? undefined
        : zonedTimeToUtc(
            new Date(Number(endTimestamp) * 1000),
            "UTC"
          )
    return {
      ended,
      endDate,
    }
  } catch (error) {
    return undefined
  }
}

import { toEther } from "utils/formatCurrency"
import { StakerUserData, UserStake } from "../types"
import { ConfigProps } from "data/types"
import { formatUnits } from "viem"

export const getUserStakes = async (
  address: string,
  stakerContract: any,
  sommelierPrice: string,
  strategyConfig: ConfigProps
) => {
  try {

    if (!stakerContract) {
      throw new Error("provider is undefined")
    }

    const userStakes = await stakerContract.read.getUserStakes([address])

    const claimAllRewards = await stakerContract.simulate.claimAll({account: address})


    let totalClaimAllRewards = BigInt(0)
    claimAllRewards.result.length &&
      claimAllRewards.result.forEach((reward: bigint) => {
        totalClaimAllRewards = totalClaimAllRewards + reward
      })

    let userStakesArray: UserStake[] = []
    let totalRewards = BigInt(0)
    let totalBondedAmount = BigInt(0)


    userStakes.forEach((item: any) => {
      const {
        amount,
        amountWithBoost,
        unbondTimestamp,
        rewardPerTokenPaid,
        rewards,
        lock,
      } = item

      totalRewards = totalRewards + BigInt(rewards)
      totalBondedAmount = totalBondedAmount + BigInt(amount)
      userStakesArray.push({
        amount: BigInt(amount),
        amountWithBoost: BigInt(amountWithBoost),
        rewardPerTokenPaid: BigInt(rewardPerTokenPaid),
        rewards: BigInt(rewards),
        unbondTimestamp,
        lock,
      })
    })

    //!!!!!!!! TODO This is hardcoded for somm
    const claimAllRewardsUSD = Number(formatUnits(totalClaimAllRewards, 6)) * Number(sommelierPrice)

    const userStakeData: StakerUserData = {
      // It's actually list of claimable rewards
      claimAllRewards: claimAllRewards.result,
      claimAllRewardsUSD,
      totalBondedAmount: {
        value: totalBondedAmount,
        formatted:
          toEther(
            totalBondedAmount,
            strategyConfig.cellar.decimals, // Must be cellar decimals
            false,
            2
          ),
      },
      totalClaimAllRewards: {
        value: totalClaimAllRewards,
        formatted:
          toEther(
            totalClaimAllRewards,
            6, // TODO: Post incentive refractor this must be the incentive asset decimals (Hardcoded for somm)
            false,
            2
          ),
      },
      totalRewards,
      userStakes: userStakesArray,
    }
    return userStakeData
  } catch (error) {
    console.warn(error)

    throw error
  }
}

import { Client, getContract } from "viem"
import { cellarDataMap } from "data/cellarDataMap"
import { AllContracts } from "../types"

export const getAllContracts = async (
  providerMap: Map<string, Client>,
  signerMap: Map<string, Client | undefined>
) => {
  let contracts: AllContracts = {}

  for (const [key, cellar] of Object.entries(cellarDataMap)) {
    const provider = providerMap.get(cellar.config.chain.id)
    const signer = signerMap.get(cellar.config.chain.id)

    // Make sure we have a provider before attempting to create a contract
    if (!provider) {
      throw new Error(
        `Provider for chain ${cellar.config.chain.id} not found`
      )
    }

    const cellarContract = getContract({
      address: cellar.config.cellar.address as `0x${string}`,
      abi: cellar.config.cellar.abi,
      client: {
        public: provider
      },
    })

    const cellarSigner = getContract({
      address: cellar.config.cellar.address as `0x${string}`,
      abi: cellar.config.cellar.abi,
      client: {
        public: provider,
        wallet: signer || undefined
      }
    })

    const stakerContract =
      cellar.config.staker &&
      getContract({
        address: cellar.config.staker.address as `0x${string}`,
        abi: cellar.config.staker.abi,
        client: {
          public: provider
        }
      })

    const stakerSigner =
      cellar.config.staker &&
      getContract({
        address: cellar.config.staker.address as `0x${string}`,
        abi: cellar.config.staker.abi,
        client: {
          public: provider,
          wallet: signer || undefined
        }
      })

    const cellarRouterSigner =
      cellar.config.cellarRouter &&
      getContract({
        address: cellar.config.cellarRouter.address as `0x${string}`,
        abi: cellar.config.cellarRouter.abi,
        client: {
          public: provider,
          wallet: signer || undefined,
        },
      })

    const boringVaultLens = 
      cellar.config.lens &&
        getContract({
          address: cellar.config.lens.address as `0x${string}`,
          abi: cellar.config.lens.abi,
          client: {
            public: provider,
          },
        })

    const boringQueue =
      cellar.config.boringQueue &&
      getContract({
        address: cellar.config.boringQueue.address as `0x${string}`,
        abi: cellar.config.boringQueue.abi,
        client: {
          public: provider,
          wallet: signer || undefined,
        },
      })

    const contract = {
      cellarContract,
      cellarSigner,
      stakerContract,
      stakerSigner,
      cellarRouterSigner,
      boringVaultLens,
      boringQueue,
      chain: cellar.config.chain.id,
    }

    let chainId = ''
    if (cellar.config.chain.id !== 'ethereum'){
      chainId = `-${cellar.config.chain.id}`
    }

    contracts = {
      ...contracts,
      [`${cellar.config.cellar.address}${chainId}`]: contract,
    }
  }

  return contracts
}

import { fetchCoingeckoPrice } from "queries/get-coingecko-price"
import { CellarNameKey, ConfigProps } from "data/types"
import { tokenConfigMap } from "data/tokenConfig"
import { formatUnits } from "viem"

const ARB_TOKENS_IN_PERIOD_ETH = 30000
const ARB_TOKENS_IN_PERIOD_USD = 7500

const OP_TOKENS_IN_PERIOD = 5000

const PERIOD_DAYS = 7

export const getMerkleRewardsApy = async (
  cellarContract: any,
  cellarConfig: ConfigProps
) => {
  let tokenPrice;
  let tokensInPeriod;

  if (cellarConfig.chain.id === "arbitrum") {
    tokenPrice = await fetchCoingeckoPrice(tokenConfigMap.ARB_ARBITRUM, "usd");

    tokensInPeriod = cellarConfig.cellarNameKey === CellarNameKey.REAL_YIELD_ETH_ARB
      ? ARB_TOKENS_IN_PERIOD_ETH
      : ARB_TOKENS_IN_PERIOD_USD;

  } else {
    tokenPrice = await fetchCoingeckoPrice(tokenConfigMap.OP_OPTIMISM, "usd");
    tokensInPeriod = OP_TOKENS_IN_PERIOD;
  }

  const baseAssetPrice = await fetchCoingeckoPrice(cellarConfig.baseAsset, "usd");
  const totalValueStaked = await fetchTotalValueStaked(cellarContract);

  const totalValueStakedInUsd =
    parseFloat(formatUnits(totalValueStaked, cellarConfig.cellar.decimals)) * Number(baseAssetPrice)

  return ((tokensInPeriod * Number(tokenPrice)) /
      totalValueStakedInUsd) *
    (365 / PERIOD_DAYS) *
    100
}
const fetchTotalValueStaked = async (
  cellarContract: any
) => {
  try {
    const totalAssets = await cellarContract.read.totalAssets()
    return totalAssets
  } catch (error) {
    console.error("Failed to fetch total value staked:", error)
    return BigInt(0)
  }
}

export const getPreviewRedeem = async ({
  cellarContract,
  value,
}: {
  cellarContract: any
  value?: string
}) => {
  if (!value) return
  try {
    const shares = await cellarContract.read.previewRedeem([value])
    return {
      value: shares,
    }
  } catch (e) {
    console.error("Error in getPreviewRedeem", e)
    return null
  }
}

import { ConfigProps } from "data/types"
import { formatUnits } from "viem"

export const getRewardsApy = async ({
  stakerContract,
  sommPrice,
  assetPrice,
  cellarConfig,
}: {
  stakerContract: any
  sommPrice: string
  assetPrice: string
  cellarConfig: ConfigProps
}) => {
  try {

    const stakingEnd = await stakerContract.read.endTimestamp()
    const isStakingOngoing = BigInt(Date.now()) < stakingEnd * 1000n;

    let potentialStakingApy = 0
    if (isStakingOngoing) {
      const rewardRateRes = await stakerContract.read.rewardRate()
      const rewardRate = formatUnits(rewardRateRes, 6)


      const totalDepositWithBoostRes =
        await stakerContract.read.totalDepositsWithBoost()
      const totalDepositWithBoost =
        formatUnits(totalDepositWithBoostRes.toString(), cellarConfig.cellar.decimals)

      // Assuming a user deposits 10k worth of the asset
      const userDeposit = 10000 / Number(assetPrice)
      const withUserDeposit = (Number(totalDepositWithBoost) + userDeposit) * Number(assetPrice)

      potentialStakingApy = Number(rewardRate)
        * Number(sommPrice)
        / withUserDeposit
        * 365 * 24 * 60 * 60 * 100

    }

    return {
      formatted: potentialStakingApy.toFixed(2) + "%",
      value: potentialStakingApy,
    }
  } catch (error) {
    console.warn(error)
    return
  }
}

import { format, isSameDay, subDays } from "date-fns"
import { getGainPct } from "utils/getGainPct"
import { GetAssetGainChartDataProps, PriceData } from "../types"
import { MarketChartResponse } from "./fetchMarketChart"

export type UsdcGainChartData = Awaited<
  ReturnType<typeof getUsdcGainChartData>
>

interface GetUsdcGainChartData extends GetAssetGainChartDataProps {
  usdcData: MarketChartResponse
}
// Shift back 1 day coin gecko price is intentional
export const getUsdcGainChartData = async (
  props: GetUsdcGainChartData
) => {
  try {
    const { day, interval, firstDate } = props
    const isDaily = interval === "daily"
    const usdcData = props.usdcData

    const usdcGainPct = (() => {
      let res: PriceData[] = []
      usdcData.prices.map(([date, value]) => {
        const firstDailyDateData =
          isDaily &&
          firstDate &&
          usdcData.prices.find((item) =>
            isSameDay(subDays(new Date(item[0]), 1), firstDate)
          )

        const firstData = isDaily
          ? firstDailyDateData
          : usdcData.prices[0]
        if (firstData) {
          res.push({
            date,
            change: getGainPct(value, firstData[1]),
            value: value,
          })
        }
      })
      return res
    })()

    let usdcMap = new Map()

    usdcGainPct.map((usdc) => {
      const getKey = (date: number) =>
        format(new Date(date), interval === "daily" ? "dLL" : "dHH")

      if (!usdcMap.has(getKey(usdc.date))) {
        usdcMap.set(getKey(usdc.date), {
          x: isDaily
            ? subDays(new Date(usdc.date), 1)
            : new Date(usdc.date),
          y: usdc.change,
          value: usdc.value.toFixed(6),
        })
      }
    })
    return {
      usdcDatum: Array.from(usdcMap, ([_, v]) => v).slice(
        isDaily ? 1 : 0
      ),
    }
  } catch (error) {
    console.warn(error)
    throw error
  }
}

