    setTokenPriceChange({
      xFormatted: dateText,
      yFormatted: `${
        valueExists ? formatPercentage(String(latestData?.y)) : "--"
      }`,
    })
  }

  // Set data to be returned by hook
  const [data, setData] = useState<DataProps>({
    series: [{ id: defaultSerieId, data: [{ x: new Date(), y: 0 }] }],
    chartProps: hourlyChartProps,
  })

  // Set tvl value
  const [tokenPriceChange, setTokenPriceChange] =
    useState<TokenPriceData>({
      xFormatted: "",
      yFormatted: "",
    })

  // Grouped loading state
  const isFetching =
    hourlyIsFetching ||
    weeklyIsFetching ||
    monthlyIsFetching ||
    allTimeIsFetching

  const isError =
    !!hourlyError || !!weeklyError || !!monthlyError || !!allTimeError

  const timeArray = [
    {
      title: "1D",
      onClick: setDataHourly,
    },
    {
      title: "1W",
      onClick: setDataWeekly,
    },
    {
      title: "1M",
      onClick: setDataMonthly,
    },
    { title: "All", onClick: setDataAllTime },
  ]

  // Set monthly data by default
  useEffect(() => {
    const idIsDefault: boolean =
      data?.series![0].id === defaultSerieId
    if (monthlyData && idIsDefault) {
      const monthlyDataMap = monthlyData?.map((item) => {
        return {
          date: item.date,
          shareValue: item.shareValue,
        }
      })
      const tokenPriceDatum =
        createTokenPriceChangeDatum(monthlyDataMap)

      const series = [
        {
          id: "token-price",
          data: tokenPriceDatum || [],
          color: colors.neutral[100],
        },
      ]

      setData({
        series,
        chartProps: dayChartProps,
      })

      const latestData = series![0].data.at(0)
      const firstData = series![0].data.at(-1)

      const dateText = `${format(
        new Date(String(firstData?.x)),
        "d MMM yyyy"
      )} - ${format(new Date(String(latestData?.x)), "d MMM yyyy")}`
      const valueExists: boolean =
        Boolean(latestData?.y) || String(latestData?.y) === "0"
      setTokenPriceChange({
        xFormatted: dateText,
        yFormatted: `${
          valueExists ? formatPercentage(String(latestData?.y)) : "--"
        }`,
      })
    }
  }, [monthlyData, data])

  const dataC = {
    ...data,
    series: data.series?.filter((item) => {
      if (item.id === "token-price") {
        return showLine.tokenPrice
      }
      return false
    }),
  }

  return (
    <tokenPriceChartContext.Provider
      value={{
        isFetching,
        isError,
        data: dataC,
        reexecuteHourly,
        reexecuteWeekly,
        reexecuteMonthly,
        reexecuteAllTime,
        timeArray,
        tokenPriceChange,
        setTokenPriceChange,
        showLine,
        setShowLine,
      }}
    >
      {children}
    </tokenPriceChartContext.Provider>
  )
}

export const useTokenPriceChart = () => {
  const context = useContext(tokenPriceChartContext)

  if (context === undefined) {
    throw new Error(
      "This hook must be used within a TokenPriceChartProvider."
    )
  }

  return context
}

import { useQuery } from "@tanstack/react-query"
import { getAllContracts } from "data/actions/common/getAllContracts"
import {
  http,
  useAccount,
  usePublicClient,
  useWalletClient,
} from "wagmi"
import { chainConfig } from "data/chainConfig"
import { INFURA_API_KEY } from "src/context/rpc_context"
import { createPublicClient } from "viem"

export const useAllContracts = () => {
  const publicClient = usePublicClient()
  const walletClient = useWalletClient()
  const { isConnected } = useAccount()

  const query = useQuery({
    queryKey: [
      "USE_ALL_STRATEGIES_CONTRACTS",
      publicClient?.chain?.id,
    ],
    queryFn: () => {
      const providerMap = new Map()
      const signerMap = new Map()

      const chainId = publicClient?.chain?.id

      chainConfig.forEach((chain) => {
        if (chain.wagmiId !== chainId) {
          providerMap.set(
            chain.id,
            createPublicClient({
              chain: chain.viemChain,
              transport: http(
                `${chain.infuraRpcUrl}/${INFURA_API_KEY}`
              ),
            })
          )
        } else {
          providerMap.set(chain.id, publicClient)
          signerMap.set(chain.id, walletClient)
        }
      })

      return getAllContracts(providerMap, signerMap)
    },
    enabled: !!publicClient?.chain?.id,
  })

  return query
}

import { useQuery } from "@tanstack/react-query"
import { getAllStrategiesData } from "data/actions/common/getAllStrategiesData"
import { useAccount, usePublicClient } from "wagmi"
import { useAllContracts } from "./useAllContracts"
import { useCoinGeckoPrice } from "./useCoinGeckoPrice"
import { fetchCellarStrategyData } from "queries/get-all-strategies-data"
import { useState, useEffect } from "react"
import { GetAllStrategiesDataQuery } from "data/actions/types"
import { tokenConfig } from "data/tokenConfig"
import {
  getChainByViemId,
  supportedChains,
} from "src/data/chainConfig"

export const useAllStrategiesData = () => {
  const { chain: viemChain } = useAccount()
  const { data: allContracts } = useAllContracts()

  const chain = getChainByViemId(viemChain?.name)

  const sommToken = tokenConfig.find((token) => {
    const compareChain = supportedChains.includes(
      chain ? chain.id : ""
    )
      ? "ethereum"
      : chain?.id
    return (
      token.coinGeckoId === "sommelier" &&
      token.chain === (compareChain || "ethereum")
    )
  })!

  const { data: sommPrice } = useCoinGeckoPrice(sommToken)

  const [cellarData, setcellarData] = useState<
    GetAllStrategiesDataQuery | undefined
  >(undefined)
  const [error, setError] = useState(null)

  useEffect(() => {
    fetchCellarStrategyData()
      .then(({ data, error }) => {
        if (error) {
          setError(error)
        } else {
          setcellarData(data)
        }
      })
      .catch((error) => setError(error))
    return () => {
      setError(null)
      setcellarData(undefined)
    }
  }, [])

  const query = useQuery({
    queryKey: ["USE_ALL_STRATEGIES_DATA", viemChain?.id],
    queryFn: async () => {
      return await getAllStrategiesData({
        allContracts: allContracts!,
        sommPrice: sommPrice!,
        cellarData: cellarData,
      })
    },
    enabled: !!allContracts && !!sommPrice && !!cellarData,
    staleTime: 120_000,
  })

  return {
    ...query,
    isError: Boolean(error) || query.isError,
  }
}

import { useState, useEffect } from "react"

// Enso Docs can be found at https://www.enso.finance/developers or https://docs.enso.finance/

const ensoApiKey = process.env.NEXT_PUBLIC_ENSO_API_KEY

export interface TokenMap {
  address: string
  amountBaseDenom: number
}

export interface EnsoRouteConfig {
  fromAddress: string
  tokensIn: TokenMap[]
  tokenOut: string
  slippage: number
}

export const getEnsoRouterAddress = async (fromAddress: string) => {
  const response = await fetch(
    `https://api.enso.finance/api/v1/wallet?chainId=1&fromAddress=${fromAddress}`,
    {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${ensoApiKey}`,
        "Access-Control-Allow-Origin": "*",
      },
    }
  )
  const routerAddress = (await response.json()).address
  return routerAddress
}

// ! Before this User must approve spend on the routing contract so it can execute the swap (if not done so already),
// ! See pt 1 on https://docs.enso.finance/examples/eoa/route-1-position
export const useEnsoRoutes = (
  config: EnsoRouteConfig,
  shouldFetch: boolean,
  lastResponse: any
) => {
  // Enso routes aren't in use for now and to avoid failing requests, returning early
  return { ensoResponse: '1', ensoError: null, ensoLoading: false };
  const [ensoResponse, setResponse] = useState<any>(null)
  const [ensoError, setError] = useState<string | null>(null)
  const [ensoLoading, setLoading] = useState<boolean>(true)

  useEffect(() => {
    if (!shouldFetch) {
      setLoading(false)
      setError(null)
      setResponse(lastResponse)
      return
    }

    let intervalId: number

    const fetchRoutes = async () => {
      setLoading(true)
      setError(null)
      try {
        const actions = config.tokensIn.map((tokenIn) => {
          return {
            protocol: "enso",
            action: "route",
            args: {
              tokenIn: tokenIn.address,
              tokenOut: config.tokenOut,
              amountIn: String(tokenIn.amountBaseDenom),
              slippage: String(config.slippage * 100),
            },
          }
        })

        // For now only support a single token in, so assert len actions == 1
        if (actions.length !== 1) {
          throw new Error(
            "Current routing implementation only support a single token in."
          )
        }

        // TODO: Generalize for multichain (check if enso actually supports multichain first)
        const formattedRouteURL = `https://api.enso.finance/api/v1/shortcuts/route?chainId=1&fromAddress=${config.fromAddress}&receiver=${config.fromAddress}&spender=${config.fromAddress}&amountIn=${actions[0].args.amountIn}&tokenIn=${actions[0].args.tokenIn}&tokenOut=${actions[0].args.tokenOut}&slippage=${actions[0].args.slippage}&routingStrategy=router`

        const response = await fetch(`${formattedRouteURL}`, {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${ensoApiKey}`,
            "Access-Control-Allow-Origin": "*",
          },
        })

        if (response.status === 404) {
          throw new Error("No routes found")
        }

        if (response.status !== 200) {
          console.log(response.status)
          throw new Error("Internal Router Error")
        }
        const result = await response.json()

        setResponse(result)
        setLoading(false)
        setError(null)
      } catch (error) {
        console.log(error)
        setError(
          (error as Error).message || "An unknown error occurred"
        )
        setLoading(false)
      }
    }
    fetchRoutes()

    // Run fetchRoutes every 10 seconds
    intervalId = setInterval(fetchRoutes, 10000) as unknown as number

    // Cleanup function to clear the interval when the component unmounts
    return () => {
      clearInterval(intervalId)
    }
  }, [config, shouldFetch])

  return { ensoResponse, ensoError, ensoLoading }
}

