import { useQuery } from "@tanstack/react-query"
import { getStrategyData } from "data/actions/common/getStrategyData"
import { cellarDataMap } from "data/cellarDataMap"
import { GetStrategyDataQuery } from "src/data/actions/types"
import { usePublicClient } from "wagmi"
import { useAllContracts } from "./useAllContracts"
import { useCoinGeckoPrice } from "./useCoinGeckoPrice"
import { fetchIndividualCellarStrategyData } from "queries/get-individual-strategy-data"
import { useState, useEffect } from "react"
import { tokenConfig } from "data/tokenConfig"

export const useStrategyData = (
  address: string,
  chain: string,
  enabled: boolean = true
) => {
  const publicClient = usePublicClient()

  const { data: allContracts } = useAllContracts()
  const sommToken = tokenConfig.find(
    (token) =>
      token.coinGeckoId === "sommelier" && token.chain === chain
  )!

  const { data: sommPrice } = useCoinGeckoPrice(sommToken)

  const [stratData, setStratData] = useState<
    GetStrategyDataQuery | undefined
  >(undefined)
  const [error, setError] = useState(null)

  const cellarData = Object.values(cellarDataMap).find(
    (item) =>
      item.config.cellar.address.toLowerCase() ===
        address.toLowerCase() && item.config.chain.id === chain
  )!

  useEffect(() => {
    fetchIndividualCellarStrategyData(
      address.toLowerCase(),
      cellarData.config.chain.id
    )
      .then(({ data, error }) => {
        if (error) {
          setError(error)
        } else {
          setStratData(data)
        }
      })
      .catch((error) => setError(error))
  }, [])

  const config = Object.values(cellarDataMap).find(
    (item) =>
      item.config.cellar.address.toLowerCase() ===
        address.toLowerCase() && item.config.chain.id === chain
  )!.config
  const isNoDataSource = Boolean(config!.isNoDataSource)
  const baseAsset = config.baseAsset
  const { data: baseAssetPrice } = useCoinGeckoPrice(baseAsset)

  // if chain is not ethereum, key format is '{address}-{chain}', otherwise it is '{address}'
  const key =
    address + (config.chain.id !== "ethereum" ? "-" + chain : "")

  // Get cellar contracts for the chain
  const query = useQuery({
    queryKey: [
      "USE_STRATEGY_DATA",
      { provider: publicClient?.uid, address: key },
    ],
    queryFn: async () => {
      const result = await getStrategyData({
        address,
        contracts: allContracts![key]!,
        sommPrice: sommPrice ?? "0",
        stratData: structuredClone(stratData?.cellar),
        baseAssetPrice: baseAssetPrice ?? "0",
      })

      return result
    },
    enabled:
      enabled &&
      !!allContracts &&
      !!sommPrice &&
      (isNoDataSource || !!stratData) &&
      !!baseAssetPrice,
  })

  return {
    ...query,
    isError: Boolean(error) || query.isError,
  }
}

import { useQuery } from "@tanstack/react-query"
import { getUserDataWithContracts } from "data/actions/common/getUserData"
import { cellarDataMap } from "data/cellarDataMap"
import { useAccount } from "wagmi"
import { useAllContracts } from "./useAllContracts"
import { useCoinGeckoPrice } from "./useCoinGeckoPrice"
import { useStrategyData } from "./useStrategyData"
import { useUserBalance } from "./useUserBalance"
import { tokenConfig } from "data/tokenConfig"
import { formatUSD } from "utils/formatCurrency"
import { showNetValueInAsset } from "data/uiConfig"

export const useUserStrategyData = (
  strategyAddress: string,
  chain: string,
  enabled: boolean = true
) => {
  const { address: userAddress } = useAccount()
  const { data: allContracts } = useAllContracts()
  const strategyData = useStrategyData(strategyAddress, chain)
  const sommToken = tokenConfig.find(
    (token) =>
      token.coinGeckoId === "sommelier" && token.chain === chain
  )!

  const sommPrice = useCoinGeckoPrice(sommToken)

  const config = Object.values(cellarDataMap).find(
    (item) =>
      item.config.cellar.address.toLowerCase() ===
        strategyAddress.toLowerCase() &&
      item.config.chain.id === chain
  )!.config

  const isNoDataSource = Boolean(
    Object.values(cellarDataMap).find(
      (item) =>
        item.config.cellar.address === strategyAddress &&
        item.config.chain.id === chain
    )?.config.isNoDataSource
  )
  const { lpToken } = useUserBalance(config)
  const baseAsset = config.baseAsset
  const { data: baseAssetPrice } = useCoinGeckoPrice(baseAsset)

  // if chain is not ethereum, key format is '{address}-{chain}', otherwise it is '{address}'
  const key =
    strategyAddress +
    (config.chain.id !== "ethereum" ? "-" + chain : "")
  const query = useQuery({
    queryKey: ["USE_USER_DATA", strategyAddress, chain, userAddress],
    queryFn: async () => {
      // Use the LP token data from useUserBalance instead of contract calls
      const lpTokenData = lpToken.data

      if (!lpTokenData || !strategyData.data) {
        return {
          userStrategyData: {
            userData: {
              netValue: { formatted: "0", value: 0 },
              shares: { formatted: "0", value: 0n },
              stakedShares: { formatted: "0", value: 0n },
              ...(showNetValueInAsset(config) && {
                netValueInAsset: { formatted: "0", value: 0 },
              }),
            },
            strategyData: strategyData.data,
          },
          userStakes: null,
        }
      }

      // Calculate net value using LP token balance and token price
      const shares = lpTokenData.value
      const sharesFormatted = lpTokenData.formatted
      const tokenPrice =
        parseFloat(
          (strategyData.data.tokenPrice || "0").replace("$", "")
        ) || 0
      const baseAssetPriceValue =
        parseFloat(baseAssetPrice || "0") || 0

      const netValue = Number(sharesFormatted) * tokenPrice

      // Calculate net value in base asset for strategies that support it
      const netValueInAsset =
        showNetValueInAsset(config) && baseAssetPriceValue > 0
          ? netValue / baseAssetPriceValue
          : 0

      return {
        userStrategyData: {
          userData: {
            netValue: {
              formatted: formatUSD(netValue.toString(), 2),
              value: netValue,
            },
            shares: {
              formatted: sharesFormatted,
              value: shares,
            },
            stakedShares: {
              formatted: "0",
              value: 0n,
            },
            ...(showNetValueInAsset(config) && {
              netValueInAsset: {
                formatted: netValueInAsset.toFixed(5),
                value: netValueInAsset,
              },
            }),
          },
          strategyData: strategyData.data,
        },
        userStakes: null,
      }
    },
    enabled:
      enabled &&
      !!userAddress &&
      !!sommPrice.data &&
      !!lpToken.data &&
      !!baseAssetPrice &&
      !!strategyData.data &&
      isNoDataSource === false,
    staleTime: 120_000,
  })
  return query
}

import { useEffect, useState } from "react"
import { useAccount, useChainId, usePublicClient, useWalletClient } from "wagmi"
import { getAddress, getContract } from "viem"
import withdrawQueueV0821 from "src/abi/withdraw-queue-v0.8.21.json"
import { useBoringQueueWithdrawals } from "./useBoringQueueWithdrawals"
import { ConfigProps } from "data/types"

export const useWithdrawRequestStatus = (
  cellarConfig: ConfigProps
) => {
  const [isActiveWithdrawRequest, setIsActiveWithdrawRequest] =
    useState(false)
  const { address, isConnected } = useAccount()
  const chainId = useChainId()
  const publicClient = usePublicClient()
  const walletClient = useWalletClient()

  const { data: boringQueueWithdrawals } = useBoringQueueWithdrawals(
    cellarConfig.cellar.address,
    cellarConfig.chain.id,
    { enabled: !!cellarConfig.boringQueue }
  )

  const withdrawQueueContract =
    publicClient &&
    getContract({
      address: getAddress(cellarConfig.chain.withdrawQueueAddress),
      abi: withdrawQueueV0821,
      client: {
        wallet: walletClient?.data,
        public: publicClient,
      },
    })

  useEffect(() => {
    const checkWithdrawRequest = async () => {
      if (chainId !== cellarConfig.chain.wagmiId) return
        try {
          if (
            walletClient &&
            withdrawQueueContract &&
            address &&
            cellarConfig &&
            !boringQueueWithdrawals
          ) {
            const withdrawRequest =
              await withdrawQueueContract.read.getUserWithdrawRequest([
                address,
                cellarConfig.cellar.address,
              ])

            const isWithdrawRequestValid =
              (await withdrawQueueContract.read.isWithdrawRequestValid([
                cellarConfig.cellar.address,
                address,
                withdrawRequest,
              ])) as unknown as boolean

            setIsActiveWithdrawRequest(isWithdrawRequestValid)
          } else if (
            boringQueueWithdrawals &&
            cellarConfig.boringVault &&
            address
          ) {
            setIsActiveWithdrawRequest(
              boringQueueWithdrawals?.open_requests.length > 0
            )
          } else {
            setIsActiveWithdrawRequest(false)
          }
        } catch (error) {
          console.error(error)
          setIsActiveWithdrawRequest(false)
        }
    }

    checkWithdrawRequest()
  }, [
    withdrawQueueContract,
    address,
    cellarConfig,
    boringQueueWithdrawals,
    walletClient,
  ])

  return isActiveWithdrawRequest
}

