// Reference: https://github.com/wagmi-dev/wagmi/blob/0.2.x/packages/react/src/hooks/transactions/useWaitForTransaction.ts
// This helper hook is created because wagmi migration from 0.2.x to >=0.3.x have breaking change, it removes `wait` function https://wagmi.sh/docs/migration-guide#usewaitfortransaction
// We need `wait` because our transaction pattern is using async functions, in >=0.3.x versions is using hook pattern
import * as React from "react"
import { useCancel } from "hooks/utils/useCancel"
import { usePublicClient } from "wagmi"
import { TransactionReceipt } from "viem/_types/types/transaction"

export type Config = {
  /**
   * Number of blocks to wait for after transaction is mined
   * @default 1
   */
  confirmations?: number
  /** Transaction hash to monitor */
  hash?: string
  /** Disables fetching */
  skip?: boolean
  /*
   * Maximum amount of time to wait before timing out in milliseconds
   * @default 0
   */
  timeout?: number
  /** Function resolving to transaction receipt */
  wait?: Object
}

type State = {
  receipt?: TransactionReceipt
  error?: Error
  loading?: boolean
}

const initialState: State = {
  loading: false,
}

export const useWaitForTransaction = ({
  confirmations,
  hash,
  skip,
  timeout,
  wait: wait_,
}: Config = {}) => {
  const publicClient = usePublicClient()
  const [state, setState] = React.useState<State>(initialState)

  const cancelQuery = useCancel()
  const wait = React.useCallback(
    async (config?: {
      confirmations?: Config["confirmations"]
      hash?: Config["hash"]
      timeout?: Config["timeout"]
      wait?: Config["wait"]
    }) => {
      let didCancel = false
      cancelQuery(() => {
        didCancel = true
      })

      try {
        const config_ = config ?? {
          confirmations,
          hash,
          timeout,
          wait: wait_,
        }
        if (!config_.hash && !config_.wait)
          throw new Error("hash or wait is required")

        let promise: Promise<TransactionReceipt>
        if (config_.wait)
          { // @ts-ignore
            promise = config_.wait(config_.confirmations)
          }
        else if (config_.hash)
          promise = publicClient!.waitForTransactionReceipt({
              confirmations: config_.confirmations,
              hash: config_.hash as `0x${string}`,
              timeout: config_.timeout
            }
          )
        else throw new Error("hash or wait is required")

        setState((x) => ({ ...x, loading: true }))
        const receipt = await promise
        if (!didCancel) {
          setState((x) => ({ ...x, loading: false, receipt }))
        }
        return { data: receipt, error: undefined }
      } catch (error_) {
        const error = <Error>error_
        if (!didCancel) {
          setState((x) => ({ ...x, error, loading: false }))
        }
        return { data: undefined, error }
      }
    },
    [cancelQuery, confirmations, hash, publicClient, timeout, wait_]
  )

  // Fetch balance when deps or chain changes
  /* eslint-disable react-hooks/exhaustive-deps */
  React.useEffect(() => {
    if (skip || (!hash && !wait_)) return
    wait({ confirmations, hash, timeout, wait: wait_ })
    return cancelQuery
  }, [cancelQuery, hash, skip, wait_])
  /* eslint-enable react-hooks/exhaustive-deps */

  return [
    {
      data: state.receipt,
      error: state.error,
      loading: state.loading,
    },
    wait,
  ] as const
}

import { useAccount, usePublicClient, useWalletClient } from "wagmi"
import { Address, erc20Abi, getAddress, getContract, parseUnits } from "viem"
import { Text } from "@chakra-ui/react"
import { useBrandedToast } from "hooks/chakra"
import { useWaitForTransaction } from "hooks/wagmi-helper/useWaitForTransactions"

export const useApproveERC20 = ({
  tokenAddress,
  spender,
}: {
  tokenAddress: string
  spender: string
}) => {
  const { addToast, update, close, closeAll } = useBrandedToast()

  const { address } = useAccount()

  const { data: walletClient } = useWalletClient()
  const publicClient = usePublicClient()

  const erc20Contract = publicClient && getContract({
    address: getAddress(tokenAddress),
    abi: erc20Abi,
    client: {
      wallet: walletClient,
      public: publicClient
    },
  })!

  const [_, wait] = useWaitForTransaction({
    skip: true,
  })

  const doApprove = async (
    amount: number,
    options?: {
      onSuccess?: () => void
      onError?: (error: Error) => void
    }
  ) => {
    const allowance = await erc20Contract?.read.allowance([
      address as Address,
      spender as Address
      ]
    ) ?? BigInt(0)
    const amtInWei = parseUnits(
      amount.toString(),
      18
    )

    let needsApproval
    try {
      needsApproval = allowance < amtInWei
    } catch (e) {
      return
    }
    if (needsApproval) {
      try {
        // @ts-ignore
        const hash = await erc20Contract?.write.approve([
          spender,
          amtInWei
          ],
          { account: address}
        )

        addToast({
          heading: "ERC20 Approval",
          status: "default",
          body: <Text>Approving ERC20</Text>,
          isLoading: true,
          closeHandler: close,
          duration: null,
        })

        const waitForApproval = wait({ confirmations: 1, hash })
        const result = await waitForApproval
        if (result?.data?.transactionHash) {
          update({
            heading: "ERC20 Approval",
            body: <Text>ERC20 Approved</Text>,
            status: "success",
            closeHandler: closeAll,
          })

          if (
            options?.onSuccess &&
            typeof options.onSuccess === "function"
          ) {
            options.onSuccess()
          }
        }

        if (result?.error) {
          update({
            heading: "ERC20 Approval",
            body: <Text>Approval Failed</Text>,
            status: "error",
            closeHandler: closeAll,
          })

          if (
            options?.onError &&
            typeof options?.onError === "function"
          ) {
            options.onError(result.error)
          }
        }
      } catch (e) {
        const error = e as Error
        console.error(error)
        addToast({
          heading: "ERC20 Approval",
          body: <Text>Approval Cancelled</Text>,
          status: "info",
          closeHandler: closeAll,
        })
        throw e
      }
    }
  }

  return { doApprove }
}

