      const needsApproval = allowance < convertedAmount

      if (needsApproval) {
        addToast({
          heading: "Loading",
          status: "default",
          body: <Text>Approval in progress</Text>,
          isLoading: true,
          duration: null,
          closeHandler: closeAll,
