      const bytes32asHex = bytesToHex(getBytes32(props.address))

      // @ts-ignore
      const bridgeHash = await bridgeContract?.write.sendToCosmos([
        tokenConfigMap.SOMM_ETHEREUM.address,
        bytes32asHex,
        convertedAmount
        ],
        { account: address }
      )
      const waitForBridge = wait({
        hash: bridgeHash,
      })
      const resultBridge = await waitForBridge

      if (resultBridge.data?.status !== "success") {
        analytics.track("bridge.contract-failed", {
          value: props.amount,
          path: "ethToSomm",
          sender: address,
          receiver: props.address,
          txHash: resultBridge.data?.transactionHash,
        })
        setIsLoading(false)
        return update({
          heading: "Bridge Initiated",
          body: (
            <TxHashToastBody
              title="Contract Failed"
              hash={bridgeHash}
            />
          ),
          status: "error",
          duration: null,
          closeHandler: closeAll,
        })
      }
      if (
        resultBridge?.data?.transactionHash &&
        resultBridge.data?.status === "success"
