      analytics.track("bridge.failed", {
        value: props.amount,
        path: "ethToSomm",
        sender: address,
        receiver: props.address,
      })
      const error = e as Error
      setIsLoading(false)
      update({
        heading: "Error",
        body: <Text>{error.message}</Text>,
        status: "error",
        closeHandler: closeAll,
      })
    }
  }

  return { isLoading, doEthToSomm }
}

import { Text } from "@chakra-ui/react"
import { useBrandedToast } from "hooks/chakra"
import { Link } from "components/Link"
import { ExternalLinkIcon } from "components/_icons"
import { useWaitForTransaction } from "hooks/wagmi-helper/useWaitForTransactions"
import { ConfigProps } from "data/types"
import { ReactNode } from "react"
import { TransactionReceipt } from "viem/_types/types/transaction"

type Result =
  | {
      data: TransactionReceipt
      error: undefined
    }
  | {
      data: undefined
      error: Error
    }

type TxParams = {
  cellarConfig: ConfigProps
  hash: string
  toastBody?: {
    info?: ReactNode
    success?: ReactNode
    error?:ReactNode
    successWithParams?: (data: Result) => ReactNode
  }
  onSuccess?: () => void
  onError?: (error: Error) => void
}
export const useHandleTransaction = (): {
  doHandleTransaction: (T: TxParams) => Promise<void>
} => {
  const { addToast, update, close, closeAll } = useBrandedToast()

  const [_, wait] = useWaitForTransaction({
    skip: true,
  })

  const doHandleTransaction = async ({
    cellarConfig,
    hash,
    toastBody,
    onSuccess,
    onError,
  }: TxParams) => {
    const infoBody = toastBody?.info || <Text>In progress...</Text>
    const errorBody = toastBody?.error || <Text>Failed</Text>

    addToast({
      heading: "Transaction",
      status: "default",
      body: infoBody,
      isLoading: true,
      closeHandler: close,
      duration: null,
    })
    const waitForApproval = wait({ confirmations: 1, hash })
    const result = await waitForApproval

    if (result?.data?.transactionHash) {
      const successBody = toastBody?.success || (
        <>
          <Text>Successful</Text>
          <Link
            display="flex"
            alignItems="center"
            href={`${cellarConfig.chain.blockExplorer.url}/tx/${result?.data?.transactionHash}`}
            isExternal
          >
            <Text as="span">{`View on ${cellarConfig.chain.blockExplorer.name}`}</Text>
            <ExternalLinkIcon ml={2} />
          </Link>
        </>
      )
      if (!!toastBody?.successWithParams) {
        const tBody = toastBody.successWithParams(result)
        update({
          heading: "Transaction",
          body: tBody,
          status: "success",
          closeHandler: closeAll,
        })
      } else {
        update({
          heading: "Transaction",
          body: successBody,
          status: "success",
          closeHandler: closeAll,
        })
      }

      if (onSuccess && typeof onSuccess === "function") {
        onSuccess()
      }
    }

    if (result?.error) {
      update({
        heading: "Transaction",
        body: errorBody,
        status: "error",
        closeHandler: closeAll,
      })

      if (onError && typeof onError === "function") {
        onError(result.error)
      }
    }
  }

  return { doHandleTransaction }
}

import { erc20Abi } from "viem"
import { usePublicClient } from "wagmi"
import { useMutation } from "@tanstack/react-query"

interface ImportTokenParams {
  address: string
  imageUrl?: string
  chain?: string
}

interface ImportTokenCallbacks {
  onSuccess?: (data: any) => void
  onError?: (error: Error) => void
}

export const useImportToken = (callbacks?: ImportTokenCallbacks) => {
  const publicClient = usePublicClient()

  return useMutation({
    mutationFn: async (params: ImportTokenParams) => {
      // Get token data
      if (!publicClient) return

      const tokenData = await publicClient.multicall({
        contracts: [
          {
            address: params.address as `0x${string}`,
            abi: erc20Abi,
            functionName: "name",
          },
          {
            address: params.address as `0x${string}`,
            abi: erc20Abi,
            functionName: "symbol",
          },
          {
            address: params.address as `0x${string}`,
            abi: erc20Abi,
            functionName: "decimals",
          },
        ],
      })

      const tokenInfo = {
        name: tokenData[0].result,
        symbol: tokenData[1].result,
        decimals: tokenData[2].result,
        address: params.address,
        imageUrl: params.imageUrl,
      }

      // Try to import to wallet (MetaMask)
      if (typeof window !== "undefined" && window.ethereum) {
        try {
          console.log("Attempting to import token to MetaMask:", {
            address: params.address,
            symbol: tokenData[1].result,
            decimals: tokenData[2].result,
            image: params.imageUrl,
          })

          await window.ethereum.request({
            method: "wallet_watchAsset",
            params: {
              type: "ERC20",
              options: {
                address: params.address,
                symbol: tokenData[1].result,
                decimals: tokenData[2].result,
                image: params.imageUrl,
              },
            },
          })

          console.log("Token import to MetaMask successful")
        } catch (error) {
          console.error("Failed to import token to wallet:", error)
          throw error
        }
      } else {
        console.warn("MetaMask not available for token import")
      }

      return tokenInfo
    },
    onSuccess: callbacks?.onSuccess,
    onError: callbacks?.onError,
  })
}

import { useRef, useCallback, useEffect, useState } from "react"
import { useConnect, useAccount } from "wagmi"
import { useBrandedToast } from "hooks/chakra"

export const useWalletConnection = () => {
  const { addToast } = useBrandedToast()
  const abortRef = useRef<AbortController | null>(null)
  const timeoutRef = useRef<NodeJS.Timeout | null>(null)
  const [isConnecting, setIsConnecting] = useState(false)
  const { connect, isPending } = useConnect()
  const { isConnected } = useAccount()

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      abortRef.current?.abort()
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
      }
    }
  }, [])

  // Monitor connection state changes and manage AbortController
  useEffect(() => {
    if (isPending && !isConnecting) {
      // Connection started - create AbortController
      setIsConnecting(true)
      abortRef.current?.abort() // Abort any existing connection
      abortRef.current = new AbortController()

      // Set 30s timeout as fallback
      timeoutRef.current = setTimeout(() => {
        if (abortRef.current) {
          abortRef.current.abort()
          addToast({
            heading: "Connection Timeout",
            body: "Connection request timed out. Please try again.",
            status: "error",
          })
        }
      }, 30000)
    } else if (!isPending && isConnecting) {
      // Connection finished (success or failure)
      setIsConnecting(false)
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current)
        timeoutRef.current = null
      }
    }
  }, [isPending, isConnecting, addToast])

  // Reset connecting state when wallet connects
  useEffect(() => {
    if (isConnected) {
      setIsConnecting(false)
    }
  }, [isConnected])

  // Function to cancel current connection
  const cancelConnection = useCallback(() => {
    if (abortRef.current) {
      abortRef.current.abort()
      addToast({
        heading: "Connection Cancelled",
        body: "Wallet connection was cancelled.",
        status: "info",
      })
    }
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current)
      timeoutRef.current = null
    }
    setIsConnecting(false)
  }, [addToast])

  // Advanced connection handler with AbortController support
  const handleConnectWithAbort = useCallback(
    async (connector: any) => {
      if (isConnecting) return

      setIsConnecting(true)
      abortRef.current?.abort()
      abortRef.current = new AbortController()

      const timer = setTimeout(() => {
        abortRef.current?.abort()
        addToast({
          heading: "Connection Timeout",
          body: "Connection request timed out. Please try again.",
          status: "error",
        })
      }, 30000)

      try {
        await connect({ connector })

        // Clear timeout on successful connection
        clearTimeout(timer)
      } catch (e: any) {
        if (
          e.name === "AbortError" ||
          abortRef.current?.signal.aborted
        ) {
          addToast({
            heading: "Connection Cancelled",
            body: "Connection canceled. Try again.",
            status: "info",
          })
        } else {
          // Handle specific wallet permission pending error
          if (
            e.message?.includes("wallet_requestPermissions") &&
            e.message?.includes("already pending")
          ) {
            addToast({
              heading: "Connection in Progress",
              body: "Please complete the wallet connection in your wallet extension. If the issue persists, try refreshing the page.",
              status: "warning",
            })
          } else {
            addToast({
              heading: "Connection Failed",
              body: e.message || "Failed to connect wallet",
              status: "error",
            })
          }
        }
      } finally {
        clearTimeout(timer)
        setIsConnecting(false)
      }
    },
    [isConnecting, connect, addToast]
  )

  return {
    isConnecting,
    cancelConnection,
    handleConnectWithAbort,
    abortController: abortRef.current,
  }
}

import { ChakraProvider, DarkMode } from "@chakra-ui/react"
import { DialogProvider } from "context/dialogContext"
import type { AppProps } from "next/app"
