export const invalidEventCharRex = /[^(\w|.|\-)]*/g

export class AnalyticsWrapper {
  client: ReturnType<typeof Analytics>
  enabled: boolean

  constructor(appName: string, plugins: any[]) {
    this.client = Analytics({
      app: appName,
      plugins,
    })

    this.enabled = false
  }

  enable() {
    this.enabled = true
  }

  track(eventName: string, payload?: Record<string, unknown>) {
    try {
      this.enabled && this.client.track(eventName, payload)
      this.enabled &&
        isSendToGTM &&
        // @ts-ignore - dataLayer is added by GTM
        window.dataLayer?.push({ event: eventName, ...payload })
    } catch (error) {
      console.error(error)
    }
  }

  // strips invalid characters
  safeTrack(eventName: string, payload?: Record<string, unknown>) {
    const clean = eventName.replace(invalidEventCharRex, "")
    this.track(clean, payload)
  }

  identify(id: string) {
    this.enabled && this.client.identify(id)
  }
}

export const analytics = new AnalyticsWrapper(appName, plugins)

if (enabled) {
  analytics.enable()
}

import { differenceInDays } from "date-fns"

export const createTokenPriceChangeDatum = (
  data?: { date: number; shareValue: string }[]
): { x: Date; y: string }[] | undefined => {
  if (!data) return

  let datum: { x: Date; y: string }[] = []
  data.map((item, index, arr) => {
    const firstData = data[data.length - 1]
    if (firstData) {
      const current = item.shareValue
      const change = firstData
        ? ((parseInt(current) - parseInt(firstData.shareValue)) /
            parseInt(firstData.shareValue)) *
          100
        : 0
      datum.push({
        x: new Date(item.date * 1000),
        y: String(change),
      })
    }
  })
  return datum
}

export const formatPercentage = (value: string) => {
  return parseFloat(value).toFixed(3)
}

// Helper function to calculate rolling averages
function rollingAverage(arr: number[], windowSize: number) {
  const result = []
  for (let i = 0; i < arr.length - windowSize + 1; i++) {
    const window = arr.slice(i, i + windowSize)
    const average = window.reduce((a, b) => a + b, 0) / windowSize
    result.push(average)
  }
  return result
}

export const createApyChangeDatum = ({
  launchEpoch,
  data,
  decimals,
  smooth,
  daysSmoothed,
  daysRendered,
}: {
  launchEpoch: number
  data?: { date: number; shareValue: string }[]
  decimals: number
  smooth: boolean
  daysSmoothed: number
  daysRendered: number
}): { x: Date; y: string, value: number }[] | undefined => {
  if (!data) return
  if (data.length < 2) return

  const datum: { x: Date; y: string, value: number }[] = []
  // Inception date (configured)
  const launchDate = new Date(launchEpoch * 1000)
  let apyValues: number[] = []

  // Make earliest data first if it's not already
  if (data[0].date > data[data.length - 1].date) {
    data.reverse()
  }

  // If we're smoothing, apply rolling average twice over the smoothDuration
  // to smooth out the APY curve
  // Don't smooth if we don't have suffieicnt data, or we're showing data from
  if (smooth && data.length > daysSmoothed && daysSmoothed > 0) {
    const smoothDuration = daysSmoothed // Set the number of days for the rolling average

    // Calculate daily APY values
    apyValues = data.map((item, index) => {
      if (index === 0) {
        // no previous day to compare with for the first day
        return 0
      } else {
        const previous = data[index - 1]
        const currentValue = Number(item.shareValue)
        const previousValue = Number(previous.shareValue)
        const dailyApy =
          (currentValue / previousValue - 1) * 365 * 100

        return dailyApy
      }
    })

    // For 30D MA -- Apply rolling average twice over the smoothDuration to smooth out the APY curve
    //if (daysRendered === 30) {
    //  apyValues = rollingAverage(apyValues, 3)
    //  apyValues = rollingAverage(apyValues, smoothDuration)
    //} else {
      // Pure MA
    apyValues = rollingAverage(apyValues, smoothDuration)
    //}
  } else {
    // Calculate overall (non daily) APY values without smoothing
    apyValues = data.map((item) => {
      const current = new Date(item.date * 1000)
      const daysSince = Math.abs(
        differenceInDays(current, launchDate)
      )

      // If days since is 0, skip the calculation to avoid dividing by 0
      if (daysSince === 0) return 0

      const currentValue = Number(item.shareValue)
      const startValue = 10 ** decimals
      const yieldGain = (currentValue - startValue) / startValue
      const apy = yieldGain * (365 / daysSince) * 100

      return apy
    })
  }

  // Drop the first value if it's 0
  if (apyValues[0] === 0) {
    apyValues.shift()
    data.shift()
  }

  // Modify apyValues to be the last daysRendered worth of values
  if (daysRendered > 0 && apyValues.length >= daysRendered) {
    apyValues.splice(0, apyValues.length - daysRendered)
    // Update all the dates as well
    data.splice(0, data.length - daysRendered)
  }

  // Construct the final datum array with the smoothed APY values
  data.forEach((item, index) => {
    const apyValue = apyValues[index]
    if (!isNaN(apyValue)) {
      datum.push({
        x: new Date(item.date * 1000),
        y: String(apyValue.toFixed(1)) + "%",
        value: apyValue,
      })
    }
  })

  return datum
}

import { pow } from "utils/bigIntHelpers"

export const gasLimitMargin = (
  gasEstimated: bigint,
  margin?: bigint
) => {
  const factor = margin ? margin : 130n
  return (gasEstimated * factor) / 100n;
}

export const estimateGasLimit = async (
  fn: Promise<bigint>,
  knownGasLimit: number,
  margin?: bigint
) => {
  try {
    const gas = await fn
    return gasLimitMargin(gas, margin).toString()
  } catch (error) {
    console.warn("Fail to estimate gas, using known gas limit")
    return knownGasLimit
  }
}

// Increase PAD values to provide larger buffer on retries
const PAD = [125n, 140n, 155n, 170n, 185n]

/**
 *
 * @example
 * const gasLimitEstimated = await estimateGasLimitWithRetry(
 *     cellarRouterSigner.estimateGas.depositAndSwap,
 *     cellarRouterSigner.simulate.depositAndSwap,
 *     [
 *       payload.cellarAddress,
 *       1,
 *       swapData,
 *       amountInWei,
 *       payload.selectedToken?.address!,
 *     ],
 *     600000,
 *     1000000
 *   )
 */
export const estimateGasLimitWithRetry = async (
  fnEstimateGas: any,
  fnCallStatic: any,
  args: any[],
  knownGasLimit: number,
  account?: string
) => {
  const gasEstimatedRes = await estimateGasLimit(
    fnEstimateGas(args),
    knownGasLimit,
    100n
  )
  let gasLimitEstimated = BigInt(gasEstimatedRes)

  let count = 1
  const maxTries = 5
  while (count <= maxTries) {
    try {
      const gasLimit = gasLimitMargin(
        gasLimitEstimated,
        PAD[count - 1]
      )

      const tx = await fnCallStatic(args, {
        gas: gasLimit,
        account: account
      })
      if (tx) {
        gasLimitEstimated = gasLimit
        break
      }
    } catch (e) {
      if (count === maxTries) {
        const lastTryGasLimit = pow(BigInt(10), BigInt(10)) // Last try limit is very high -- users hate the gas limits
        try {
          const tx = await fnCallStatic(args, {
            gas: lastTryGasLimit,
            account: account
          })
          if (tx) {
            gasLimitEstimated = lastTryGasLimit
          }
        } catch (error) {
          throw new Error("GAS_LIMIT_ERROR", { cause: error })
        }
      }
      count++
    }
  }
  return gasLimitEstimated
}

import { formatUnits } from "viem"

export const formatCurrency = (value?: string) => {
  const v =
    value &&
    Intl.NumberFormat("en-US", {
      notation: "compact",
      maximumFractionDigits: 2,
      minimumFractionDigits: 2,
    }).format(parseFloat(value))

  return v
}

export const formatUSD = (value?: string, maximumDigit?: number) => {
  const v =
    value &&
    Intl.NumberFormat("en-US", {
      notation: "compact",
      style: "currency",
      currency: "USD",
      maximumFractionDigits: maximumDigit || 2,
      minimumFractionDigits: 2,
    }).format(parseFloat(value))

  return v
}

export const toEther = (
  val: any | undefined,
  decimals = 18,
  format?: boolean | number,
  precision = 2
) => {
  if (val != 0n && (!val || val === "--")) return "--"
  try {
    if (typeof val === 'bigint') val = formatUnits(val, decimals)
    const result = parseFloat(val)
    if (format) {
      format = typeof format === "boolean" ? 2 : format
      return result.toLocaleString("en-US", { minimumFractionDigits: format, maximumFractionDigits: format });
    }
    return floorToPrecision(result, precision).toString()
  } catch (e) {
    console.log(e)
    return "--"
  }
}
const floorToPrecision = (num: number, precision: number) => {
  const factor = Math.pow(10, precision);
  return Math.floor(num * factor) / factor;
};

export type TransactionType =
  | "deposit"
  | "withdraw"
  | "approve"
  | "bond"
  | "claim"
  | "bridge"

export type TransactionErrorType =
  | "USER_REJECTED"
  | "GAS_LIMIT_ERROR"
  | "SPAM_FILTER_ERROR"
  | "OTHER"

export interface TransactionError {
  type: TransactionErrorType
  message: string
  originalError: Error
}

export interface TransactionErrorContext {
  vaultName: string
  tokenSymbol?: string
  transactionType: TransactionType
  value?: number
  chainId?: number
}

/**
 * Normalizes transaction errors to handle user rejections consistently
 */
export const handleTransactionError = (
  error: Error,
  context: TransactionErrorContext
): TransactionError => {
  // Check for user rejection patterns
  const anyErr: any = error as any
  const combined = [
    anyErr?.message,
    anyErr?.shortMessage,
    anyErr?.cause?.message,
    anyErr?.cause?.shortMessage,
    anyErr?.walk?.()?.message,
  ]
    .filter(Boolean)
    .join(" | ")

  const isUserRejected =
    anyErr?.code === 4001 ||
    combined.includes("User rejected") ||
    combined.includes("User denied") ||
    combined.includes("MetaMask Tx Signature: User denied") ||
    combined.includes("User rejected the request")

  if (isUserRejected) {
    return {
      type: "USER_REJECTED",
      message: "Transaction was rejected by user",
      originalError: error,
    }
  }

  // Check for spam filter errors
  if (
    error.message.includes("spam filter") ||
    error.message.includes("not been authorized")
  ) {
    return {
      type: "SPAM_FILTER_ERROR",
      message: "Transaction blocked by spam filter",
      originalError: error,
    }
  }

  // Check for gas limit errors
  if (error.message === "GAS_LIMIT_ERROR") {
    return {
      type: "GAS_LIMIT_ERROR",
      message: "Gas limit exceeded",
      originalError: error,
    }
  }

  // Default to other error
  return {
    type: "OTHER",
    message: error.message,
    originalError: error,
  }
}

/**
 * Generates consistent toast messages for transaction errors
 */
export const getTransactionErrorToast = (
  error: TransactionError,
  context: TransactionErrorContext
) => {
  const { vaultName, transactionType } = context

  switch (error.type) {
    case "USER_REJECTED":
      return {
        heading: `${vaultName} ${getTransactionTypeDisplay(
          transactionType
        )}`,
        body: `Transaction was rejected`,
        status: "info" as const,
        showPopupGuidance: true,
      }

    case "SPAM_FILTER_ERROR":
      return {
        heading: "Transaction Blocked",
        body: `Your transaction was blocked by MetaMask's spam filter. Please try:
 Clearing MetaMask activity data
 Waiting a few minutes before retrying
 Using a different amount`,
        status: "warning" as const,
      }

    case "GAS_LIMIT_ERROR":
      return {
        heading: "Transaction not submitted",
        body: `Your transaction has failed. If it does not work after waiting some time and retrying, please contact support.`,
        status: "info" as const,
      }

    default:
      return {
        heading: `${vaultName} ${getTransactionTypeDisplay(
          transactionType
        )}`,
        body: error.message,
        status: "error" as const,
      }
  }
}

/**
 * Converts transaction type to display format
 */
const getTransactionTypeDisplay = (type: TransactionType): string => {
  switch (type) {
    case "deposit":
      return "Deposit"
    case "withdraw":
      return "Withdraw"
    case "approve":
      return "Approval"
    case "bond":
      return "Bond"
    case "claim":
      return "Claim"
    case "bridge":
      return "Bridge"
    default:
      return "Transaction"
  }
}

/**
 * Generates analytics data for transaction errors
 */
export const getTransactionErrorAnalytics = (
  error: TransactionError,
  context: TransactionErrorContext
) => {
  return {
    vaultName: context.vaultName,
    tokenSymbol: context.tokenSymbol,
    transactionType: context.transactionType,
    value: context.value,
    chainId: context.chainId,
    message: error.type,
    errorMessage: error.message,
  }
}

import { parseMoneyString, coerceNetValue } from "./money"

export type Vault = {
  name?: string
  metrics?: { tvl?: number | null }
  user?: { netValue?: number | null }
}

const normName = (s?: string) => (s ?? "").toLowerCase()

export function sortVaultsForMainPage(
  vaults: any[],
  opts: { connected: boolean }
) {
  const byTVL = (v: any) => parseMoneyString(v.tvl)
  const byNV = (v: any) => coerceNetValue(v.netValue)

  // Debug logging for sorting
  if (process.env.NEXT_PUBLIC_DEBUG_SORT === "1") {
    console.log("Sorting vaults:", {
      connected: opts.connected,
      vaultCount: vaults.length,
      sampleVaults: vaults.slice(0, 3).map((v) => ({
        name: v.name,
        tvl: v.tvl,
        netValue: v.netValue,
        parsedTvl: byTVL(v),
        parsedNetValue: byNV(v),
      })),
    })
  }

  return [...vaults].sort((a, b) => {
    if (!opts.connected) return byTVL(b) - byTVL(a)
    const an = byNV(a),
      bn = byNV(b)
    if (Number.isFinite(an) && Number.isFinite(bn) && an !== bn)
      return bn - an
    // fallback TVL
    return byTVL(b) - byTVL(a)
  })
}

// Keep the original function for backward compatibility
export function sortVaults<T extends Vault>(
  list: T[],
  isConnected: boolean
): T[] {
  return [...list].sort((a, b) => {
    if (isConnected) {
      // When connected: Order by Net Value (highest to lowest), fallback to TVL
      const aNetValue = coerceNetValue(a.user?.netValue)
      const bNetValue = coerceNetValue(b.user?.netValue)

      // Compare net values first
      const nv = bNetValue - aNetValue
      if (nv !== 0) return nv

      // Fallback to TVL if net values are equal, 0, or missing
      const aTvl = parseMoneyString(a.metrics?.tvl)
      const bTvl = parseMoneyString(b.metrics?.tvl)
      const tvl = bTvl - aTvl
      if (tvl !== 0) return tvl
    } else {
      // When not connected: Order by TVL (highest to lowest)
      const aTvl = parseMoneyString(a.metrics?.tvl)
      const bTvl = parseMoneyString(b.metrics?.tvl)
      const tvl = bTvl - aTvl
      if (tvl !== 0) return tvl
    }

    // Final fallback: alphabetical by name
    const an = normName(a.name)
    const bn = normName(b.name)
    return an < bn ? -1 : an > bn ? 1 : 0
  })
}

import { createClient } from "@supabase/supabase-js"

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL ?? ""
const supabasePubKey = process.env.NEXT_PUBLIC_SUPABASE_PUB_KEY ?? ""

class SupabaseNoop {
  async from(tableName: string) {
    return { insert: function (options: any) {} }
  }
}

// Create a single supabase client for interacting with your database
const enabled = supabaseUrl.length > 0 && supabasePubKey.length > 0

let supabase: any = new SupabaseNoop()
if (enabled) {
  supabase = createClient(supabaseUrl, supabasePubKey)
}

export async function insertEvent(options: {
  event: string
  address: string
  cellar?: string
  transaction_hash?: string
}) {
  if (!enabled) return

  const { event, address, cellar, transaction_hash } = options
  const user_agent = window.navigator.userAgent

  switch (event) {
    case "wallet.connect-succeeded": {
      await supabase
        .from("event_connect")
        .insert({ address, user_agent })
      break
    }
    case "deposit.started": {
      await supabase
        .from("event_deposit_started")
        .insert({ address, cellar, user_agent })
      break
    }
    case "deposit.succeeded": {
      await supabase.from("event_deposit_success").insert({
        address,
        cellar,
        transaction_hash,
        user_agent,
      })
      break
    }
  }
}

import { switchChain } from "wagmi/actions"
import { config } from "../../lib/wagmi"
import { chainConfig } from "data/chainConfig"

export async function requestSwitch(chainId: 1 | 42161 | 8453) {
  try {
    await switchChain(config, { chainId })
    return true
  } catch (e: any) {
    if (e?.code === 4001) throw new Error("User canceled chain switch")
    if (e?.message?.includes("Unrecognized chain ID")) {
      throw new Error("Unsupported chain in wallet")
    }
    throw e
  }
}

export async function requestSwitchWithAdd(chainId: 1 | 42161 | 8453) {
  try {
    await switchChain(config, { chainId })
    return true
  } catch (e: any) {
    // If chain not added in wallet, try wallet_addEthereumChain
    if (e?.code === 4902 && typeof window !== "undefined" && (window as any)?.ethereum) {
      const chain = chainConfig.find((c) => c.wagmiId === chainId)
      if (!chain) {
        throw new Error("Chain configuration not found")
      }

      try {
        const rpcUrls = [
          (chain as any).infuraRpcUrl,
          (chain as any).alchemyRpcUrl,
          (chain as any).quicknodeRpcUrl,
        ].filter(Boolean)

        await (window as any).ethereum.request({
          method: "wallet_addEthereumChain",
          params: [
            {
              chainId: "0x" + chainId.toString(16),
              chainName: chain.displayName,
              rpcUrls: rpcUrls.length ? rpcUrls : undefined,
              nativeCurrency: {
                name: "ETH",
                symbol: "ETH",
                decimals: 18,
              },
              blockExplorerUrls: [
                chain.blockExplorer?.url,
              ].filter(Boolean),
            },
          ],
        })

        // Try switching again after adding
        await switchChain(config, { chainId })
        return true
      } catch (addErr: any) {
        throw new Error(`Failed to add network: ${addErr?.message ?? "Unknown error"}`)
      }
    }

    // Handle other errors
    if (e?.code === 4001) throw new Error("User canceled chain switch")
    if (e?.message?.includes("Unrecognized chain ID")) {
      throw new Error("Unsupported chain in wallet")
    }
    throw e
  }
}

export function getChainById(chainId: 1 | 42161 | 8453) {
  return chainConfig.find((c) => c.wagmiId === chainId)
}

export function getChainDisplayName(chainId: 1 | 42161 | 8453): string {
  const chain = getChainById(chainId)
  return chain?.displayName || `Chain ${chainId}`
}

/**
 * Wallet conflict resolution utilities
 */

import { getSafeEthereumProvider } from "./initOnce"

// Detect multiple wallet extensions
export function hasWalletConflicts(): boolean {
  if (typeof window === "undefined") return false

  const ethereum = getSafeEthereumProvider()
  if (!ethereum) return false

  // Check for multiple wallet providers
  let providerCount = 0

  if (ethereum.isMetaMask) {
    providerCount++
  }

  if (ethereum.isCoinbaseWallet) {
    providerCount++
  }

  if (ethereum.isWalletConnect) {
    providerCount++
  }

  // Check for generic injected wallets that aren't the main ones
  if (
    !ethereum.isMetaMask &&
    !ethereum.isCoinbaseWallet &&
    !ethereum.isWalletConnect
  ) {
    providerCount++
  }

  return providerCount > 1
}

// Get a list of detected wallet providers
export function getDetectedWalletProviders(): string[] {
  if (typeof window === "undefined") return []

  const providers: string[] = []
  const ethereum = getSafeEthereumProvider()

  if (!ethereum) return providers

  if (ethereum.isMetaMask) {
    providers.push("MetaMask")
  }

  if (ethereum.isCoinbaseWallet) {
    providers.push("Coinbase Wallet")
  }

  if (ethereum.isWalletConnect) {
    providers.push("WalletConnect")
  }

  // Check for other injected wallets
  if (
    !ethereum.isMetaMask &&
    !ethereum.isCoinbaseWallet &&
    !ethereum.isWalletConnect
  ) {
    providers.push("Injected Wallet")
  }

  return providers
}

// Determine the recommended wallet provider
export function getRecommendedProvider(): string {
  if (typeof window === "undefined") return "MetaMask"

  const ethereum = getSafeEthereumProvider()
  if (!ethereum) return "MetaMask"

  // Priority order: MetaMask > Coinbase Wallet > WalletConnect > Other
  if (ethereum.isMetaMask) return "MetaMask"
  if (ethereum.isCoinbaseWallet) return "Coinbase Wallet"
  if (ethereum.isWalletConnect) return "WalletConnect"

  return "Injected Wallet"
}

// Generate instructions for resolving conflicts
export function getConflictResolutionInstructions(): string[] {
  const providers = getDetectedWalletProviders()
  const recommended = getRecommendedProvider()

  const instructions = [
    `Multiple wallet extensions detected: ${providers.join(", ")}`,
    `Recommended provider: ${recommended}`,
    "",
    "To resolve conflicts:",
    "1. Disable or remove unused wallet extensions",
    "2. Keep only the wallet extension you want to use",
    "3. Refresh the page after making changes",
    "4. Try connecting again",
  ]

  return instructions
}

// Get debug information about wallet providers
export function getProviderDebugInfo(): any {
  if (typeof window === "undefined") return { error: "Not in browser" }

  try {
    const ethereum = getSafeEthereumProvider()
    if (!ethereum) return { error: "No ethereum provider found" }

    return {
      hasConflicts: hasWalletConflicts(),
      detectedProviders: getDetectedWalletProviders(),
      recommendedProvider: getRecommendedProvider(),
      ethereum: {
        isMetaMask: ethereum.isMetaMask,
        isCoinbaseWallet: ethereum.isCoinbaseWallet,
        isWalletConnect: ethereum.isWalletConnect,
        isConnected: ethereum.isConnected,
        selectedAddress: ethereum.selectedAddress,
        chainId: ethereum.chainId,
      },
      timestamp: new Date().toISOString(),
    }
  } catch (error) {
    return { error: error instanceof Error ? error.message : "Unknown error" }
  }
}

// Get wallet provider information for debugging
export function getWalletProviderInfo() {
  if (typeof window === "undefined") {
    return {
      ethereum: false,
      isMetaMask: false,
      isCoinbaseWallet: false,
      isWalletConnect: false,
      hasConflicts: false,
      detectedProviders: [],
      recommendedProvider: "MetaMask",
    }
  }

  const ethereum = getSafeEthereumProvider()
  const hasConflicts = hasWalletConflicts()
  const detectedProviders = getDetectedWalletProviders()
  const recommendedProvider = getRecommendedProvider()

  return {
    ethereum: !!ethereum,
    isMetaMask: ethereum?.isMetaMask || false,
    isCoinbaseWallet: ethereum?.isCoinbaseWallet || false,
    isWalletConnect: ethereum?.isWalletConnect || false,
    hasConflicts,
    detectedProviders,
    recommendedProvider,
  }
}

/**
 * Wallet connection utility functions
 */
import { getSafeEthereumProvider } from "./initOnce"

// Check if there's a pending wallet request
export function hasPendingWalletRequest(): boolean {
  if (typeof window === "undefined") return false

  // Check if ethereum provider exists and has pending requests
  const ethereum = getSafeEthereumProvider()
  if (ethereum) {
    // This is a heuristic - we can't directly check pending requests
    // but we can check if the provider is in a connecting state
    return false // Default to false, will be handled by error catching
  }

  return false
}

// Wait for wallet to be ready
export function waitForWalletReady(): Promise<void> {
  return new Promise((resolve, reject) => {
    if (typeof window === "undefined") {
      resolve()
      return
    }

    const ethereum = getSafeEthereumProvider()
    if (ethereum) {
      resolve()
      return
    }

    // Wait for ethereum provider to be injected
    const checkEthereum = () => {
      const provider = getSafeEthereumProvider()
      if (provider) {
        resolve()
      } else {
        setTimeout(checkEthereum, 100)
      }
    }

    checkEthereum()
  })
}

// Safe wallet connection with retry logic
export async function safeWalletConnect(
  connectFn: () => Promise<any>,
  maxRetries: number = 3
): Promise<any> {
  let lastError: Error | null = null

  for (let attempt = 1; attempt <= maxRetries; attempt++) {
    try {
      await waitForWalletReady()
      return await connectFn()
    } catch (error) {
      lastError = error as Error

      // If it's a permission pending error, don't retry
      if (
        error instanceof Error &&
        error.message?.includes("wallet_requestPermissions") &&
        error.message?.includes("already pending")
      ) {
        throw error
      }

      // If it's a provider conflict error, don't retry
      if (
        error instanceof Error &&
        error.message?.includes("Cannot set property ethereum")
      ) {
        throw error
      }

      // Wait before retrying
      if (attempt < maxRetries) {
        await new Promise((resolve) =>
          setTimeout(resolve, 1000 * attempt)
        )
      }
    }
  }

  throw (
    lastError ||
    new Error("Wallet connection failed after all retries")
  )
}

// Check if wallet is supported
export function isWalletSupported(walletName: string): boolean {
  if (typeof window === "undefined") return false

  const walletNameLower = walletName.toLowerCase()

  // Check for MetaMask
  if (walletNameLower === "metamask") {
    const ethereum = getSafeEthereumProvider()
    return !!ethereum?.isMetaMask
  }

  // For other wallets, check if ethereum provider exists
  if (walletNameLower === "walletconnect" || walletNameLower === "coinbase") {
    const ethereum = getSafeEthereumProvider()
    return !!ethereum
  }

  return false
}

// Get available wallet providers
export function getAvailableWalletProviders(): string[] {
  if (typeof window === "undefined") return []

  const providers: string[] = []
  const ethereum = getSafeEthereumProvider()

  if (ethereum) {
    if (ethereum.isMetaMask) providers.push("MetaMask")
    if (ethereum.isCoinbaseWallet) providers.push("Coinbase Wallet")
    if (ethereum.isWalletConnect) providers.push("WalletConnect")
  }

  return providers
}

// Get recommended wallet provider
export function getRecommendedWalletProvider(): string | null {
  if (typeof window === "undefined") return null

  const ethereum = getSafeEthereumProvider()
  if (!ethereum) return null

  // Priority order: MetaMask > Coinbase Wallet > WalletConnect
  if (ethereum.isMetaMask) return "MetaMask"
  if (ethereum.isCoinbaseWallet) return "Coinbase Wallet"
  if (ethereum.isWalletConnect) return "WalletConnect"

  return null
}

import { onCLS, onFCP, onLCP, onTTFB, onINP } from "web-vitals"

function sendToAnalytics(metric: any) {
  const { name, value, id } = metric

  // Get current page path
  const page = window.location.pathname
  // Tag route & state for analysis
  const route = page.startsWith("/vaults") ? "vaults" : page
  const state = "with_legacy_deferred"
  const userAgent = navigator.userAgent

  // Send to our API endpoint
  fetch("/api/vitals", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      name,
      value,
      id,
      page,
      route,
      state,
      userAgent,
    }),
  }).catch(console.error)
}

export function reportWebVitals() {
  onCLS(sendToAnalytics)
  onFCP(sendToAnalytics)
  onLCP(sendToAnalytics)
  onTTFB(sendToAnalytics)
  onINP(sendToAnalytics)
}
